import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import "./ManageServices.css";

const ManageServices = () => {
  const { token } = useContext(AuthContext);
  const [services, setServices] = useState([]);
  const [reason, setReason] = useState("");

  const fetchServices = async () => {
    try {
      const res = await axios.get(
        "http://localhost:5000/api/admin/services",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setServices(res.data);
    } catch (err) {
      console.error("FETCH SERVICES ERROR üëâ", err.response?.data || err);
    }
  };

  useEffect(() => {
    fetchServices();
  }, []);

  const updateStatus = async (id, status) => {
    if (status === "Rejected" && !reason) {
      alert("Please enter rejection reason");
      return;
    }

    await axios.put(
      `http://localhost:5000/api/admin/services/${id}/status`,
      {
        status,
        adminRemark: reason,
      },
      {
        headers: { Authorization: `Bearer ${token}` },
      }
    );

    setReason("");
    fetchServices();
  };

  return (
    <div className="admin-services">
      <h2>Service Applications</h2>

      {services.length === 0 && <p>No applications found</p>}

      {services.map((s) => (
        <div key={s._id} className="service-card">
          {/* USER INFO */}
          <p><b>User:</b> {s.user?.fullName}</p>
          <p><b>Email:</b> {s.user?.email}</p>
          <p><b>Mobile:</b> {s.user?.mobile}</p>
          <p><b>Village:</b> {s.user?.village}</p>

          {/* SERVICE INFO */}
          <p><b>Service:</b> {s.serviceType}</p>
          <p><b>Status:</b> {s.status}</p>
          <p><b>Applied On:</b> {new Date(s.createdAt).toLocaleDateString()}</p>

          {/* üìÑ DOCUMENTS */}
          {s.documents && Object.keys(s.documents).length > 0 && (
            <div className="documents">
              <h4>Uploaded Documents</h4>
              <ul>
                {Object.entries(s.documents).map(([key, path]) => (
                  <li key={key}>
                    üìé {key} ‚Äî{" "}
                    <a
                      href={`http://localhost:5000/${path}`}
                      target="_blank"
                      rel="noreferrer"
                    >
                      View
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* ACTIONS */}
          {s.status === "Pending" && (
            <>
              <button
                onClick={() => updateStatus(s._id, "Approved")}
                className="approve"
              >
                Approve
              </button>

              <textarea
                placeholder="Reason for rejection"
                value={reason}
                onChange={(e) => setReason(e.target.value)}
              />

              <button
                onClick={() => updateStatus(s._id, "Rejected")}
                className="reject"
              >
                Reject
              </button>
            </>
          )}

          {s.status === "Rejected" && (
            <p className="reject-text">
              ‚ùå Reason: {s.adminRemark}
            </p>
          )}
        </div>
      ))}
    </div>
  );
};

export default ManageServices;
