import React, { useEffect, useState } from "react";
import "./UserNotices.css";

const Notices = () => {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/notices");
        const data = await res.json();
        setNotices(data);
        setLoading(false);
      } catch (error) {
        console.error("Failed to load notices");
        setLoading(false);
      }
    };

    fetchNotices();
  }, []);

  if (loading) {
    return <p className="loading-text">Loading notices...</p>;
  }

  return (
    <div className="user-notice-section">
      <h2>Latest Notices & Announcements</h2>

      {notices.length === 0 ? (
        <p>No notices available</p>
      ) : (
        <div className="user-notice-list">
          {notices.map((notice) => (
            <div
              key={notice._id}
              className={`user-notice-card ${
                notice.isImportant ? "important" : ""
              }`}
            >
              <h3>{notice.title}</h3>
              <p>{notice.description}</p>
              {notice.isImportant && (
                <span className="important-badge">Important</span>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Notices;
