import React, { useState, useEffect } from 'react';

// The base URL for your public API
const API_URL = "http://localhost:5000/api/notices"; 

const ManageNotices = () => {
    const [notices, setNotices] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchNotices = async () => {
            try {
                const response = await fetch(API_URL);

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setNotices(data);
            } catch (err) {
                console.error("Fetch error:", err);
                setError("Could not load notices. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        fetchNotices();
    }, []);

    if (loading) {
        return <div className="notice-board-loading">Loading public announcements...</div>;
    }

    if (error) {
        return <div className="notice-board-error">{error}</div>;
    }

    return (
        <div className="notice-board-container">
            <h2>ðŸ“¢ Latest Gram Panchayat Notices</h2>
            {notices.length === 0 ? (
                <p>There are no current announcements.</p>
            ) : (
                <ul className="notices-list">
                    {notices.map((notice) => (
                        <li key={notice._id} className={`notice-item ${notice.priority === 'Important' ? 'notice-important' : ''}`}>
                            <h3 className="notice-title">{notice.title}</h3>
                            <p className="notice-message">{notice.message}</p>
                            <span className="notice-date">
                                Published: {new Date(notice.createdAt).toLocaleDateString()}
                            </span>
                            {notice.priority === 'Important' && (
                                <span className="notice-priority-tag">Important</span>
                            )}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ManageNotices;
