import React, { useEffect, useState } from "react";
import "./ManageDevelopment.css";

const ManageDevelopment = () => {
  const [projects, setProjects] = useState([]);
  const [images, setImages] = useState([]);
  const [editingId, setEditingId] = useState(null);
 // const [editingId, setEditingId] = useState(null);
const [newProgress, setNewProgress] = useState("");


  const [form, setForm] = useState({
    projectName: "",
    description: "",
    progress: 0,
    fundsUsed: "",
  });

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    const res = await fetch("http://localhost:5000/api/development");
    const data = await res.json();
    setProjects(Array.isArray(data) ? data : []);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ADD / UPDATE PROJECT
  const submitProject = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    Object.keys(form).forEach((key) =>
      formData.append(key, form[key])
    );

    for (let i = 0; i < images.length; i++) {
      formData.append("images", images[i]);
    }

    const url = editingId
      ? `http://localhost:5000/api/development/${editingId}`
      : "http://localhost:5000/api/development";

    const method = editingId ? "PUT" : "POST";

    await fetch(url, {
      method,
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    resetForm();
    fetchProjects();
  };

  const startEdit = (project) => {
    setEditingId(project._id);
    setForm({
      projectName: project.projectName,
      description: project.description,
      progress: project.progress,
      fundsUsed: project.fundsUsed,
    });
  };
  const markCompleted = async (id) => {
  await fetch(`http://localhost:5000/api/development/${id}`, {
    method: "PUT",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    },
    body: JSON.stringify({ progress: 100 }),
  });

  fetchProjects();
};


  const deleteProject = async (id) => {
    if (!window.confirm("Delete this project?")) return;

    await fetch(`http://localhost:5000/api/development/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    fetchProjects();
  };

  const resetForm = () => {
    setEditingId(null);
    setImages([]);
    setForm({
      projectName: "",
      description: "",
      progress: 0,
      fundsUsed: "",
    });
  };

  return (
    <div className="manage-development">
      <h2>Village Development Projects</h2>

      {/* FORM */}
      <form className="dev-form" onSubmit={submitProject}>
        <input
          name="projectName"
          placeholder="Project Name"
          value={form.projectName}
          onChange={handleChange}
          required
        />

        <textarea
          name="description"
          placeholder="Description"
          value={form.description}
          onChange={handleChange}
        />

        <input
          name="fundsUsed"
          placeholder="Funds Used (₹)"
          value={form.fundsUsed}
          onChange={handleChange}
        />

        <input
          type="number"
          name="progress"
          placeholder="Progress %"
          value={form.progress}
          onChange={handleChange}
          min="0"
          max="100"
          required
        />

        <input
          type="file"
          multiple
          onChange={(e) => setImages(e.target.files)}
        />

        <button type="submit">
          {editingId ? "Update Project" : "Add Project"}
        </button>

        {editingId && (
          <button type="button" className="cancel-btn" onClick={resetForm}>
            Cancel
          </button>
        )}
      </form>

      {/* PROJECT CARDS */}
      <div className="dev-list">
  {projects.map((p) => (
    <div className="dev-card" key={p._id}>
      <h4>{p.projectName}</h4>
      <p>{p.description}</p>

      <span
        className={`dev-status ${
          p.status === "Completed" ? "completed" : "ongoing"
        }`}
      >
        {p.status}
      </span>

      <div className="progress-bar">
        <div
          className="progress-fill"
          style={{ width: `${p.progress}%` }}
        ></div>
      </div>

      <p><b>Progress:</b> {p.progress}%</p>
      <p><b>Funds Used:</b> ₹{p.fundsUsed}</p>

      {/* IMAGES */}
      {p.images?.length > 0 && (
        <div className="dev-images">
          {p.images.map((img, i) => (
            <img
              key={i}
              src={`http://localhost:5000${img}`}
              alt="Development"
            />
          ))}
        </div>
      )}

      {/* MARK COMPLETED BUTTON */}
      {p.status !== "Completed" && (
        <button
          className="complete-btn"
          onClick={() => markCompleted(p._id)}
        >
          Mark Completed
        </button>
      )}

      {p.status === "Completed" && (
        <p className="done-text">✅ Project Completed</p>
      )}
    </div>
  ))}
</div>

    </div>
  );
};

export default ManageDevelopment;
