import React, { useEffect, useState } from "react";
import "./ManageContacts.css";

const ManageContacts = () => {
  const token = localStorage.getItem("token");

  if (!token) return <p>Please login as admin</p>;

  const [members, setMembers] = useState([]);
  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [photo, setPhoto] = useState(null);

  const fetchMembers = async () => {
    const res = await fetch("http://localhost:5000/api/contacts");
    const data = await res.json();
    setMembers(data);
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  const addMember = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("role", role);
    formData.append("phone", phone);
    formData.append("email", email);
    formData.append("photo", photo);

    await fetch("http://localhost:5000/api/contacts", {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    setName(""); setRole(""); setPhone(""); setEmail(""); setPhoto(null);
    fetchMembers();
  };

  return (
    <div className="manage-container">
      <h2>Manage Members</h2>

      <form onSubmit={addMember} className="member-form">
        <input placeholder="Name" value={name} onChange={e=>setName(e.target.value)} />
        <input placeholder="Role" value={role} onChange={e=>setRole(e.target.value)} />
        <input placeholder="Phone" value={phone} onChange={e=>setPhone(e.target.value)} />
        <input placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
        <input type="file" onChange={e=>setPhoto(e.target.files[0])} />
        <button>Add</button>
      </form>

      <div className="member-grid">
        {members.map(m => (
          <div className="member-card" key={m._id}>
            <img src={`http://localhost:5000${m.photo}`} alt="" />
            <h4>{m.name}</h4>
            <p>{m.role}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageContacts;
