import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [notices, setNotices] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [message, setMessage] = useState("");
  const [viewNotice, setViewNotice] = useState(null);

  const token = localStorage.getItem("token");

  const fetchNotices = async () => {
    const res = await fetch("http://localhost:5000/api/notices");
    const data = await res.json();
    setNotices(data);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  // ADD
  const addNotice = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/api/notices", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ title, description }),
    });

    setTitle("");
    setDescription("");
    setMessage("‚úÖ Notice added successfully");
    fetchNotices();
  };

  // DELETE
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    await fetch(`http://localhost:5000/api/notices/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    setMessage("üóë Notice deleted");
    fetchNotices();
  };

  // START EDIT
  const startEdit = (notice) => {
    setEditingId(notice._id);
    setEditTitle(notice.title);
    setEditDescription(notice.description);
  };

  // UPDATE
  const updateNotice = async (id) => {
    await fetch(`http://localhost:5000/api/notices/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        title: editTitle,
        description: editDescription,
      }),
    });

    setEditingId(null);
    setMessage("‚úèÔ∏è Notice updated successfully");
    fetchNotices();
  };

  return (
    <div className="manage-notices">
      <h2>Manage Notices</h2>
      {message && <p className="success-msg">{message}</p>}

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Add Notice</button>
      </form>

      {/* NOTICE CARDS */}
      <div className="notice-list">
        {notices.map((n) => (
          <div className="notice-card" key={n._id}>
            {editingId === n._id ? (
              <>
                <input
                  value={editTitle}
                  onChange={(e) => setEditTitle(e.target.value)}
                />
                <textarea
                  value={editDescription}
                  onChange={(e) => setEditDescription(e.target.value)}
                />
                <button onClick={() => updateNotice(n._id)}>Save</button>
                <button onClick={() => setEditingId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <h4>{n.title}</h4>
                <p>{n.description}</p>
                <div className="card-actions">
                  <button className="view-btn" onClick={() => setViewNotice(n)}>
                    View
                  </button>
                  <button
                    className="delete-btn"
                    onClick={() => deleteNotice(n._id)}
                  >
                    Delete
                  </button>
                </div>
                
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageNotices;
