import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const ManageNotices = () => {
  const token = localStorage.getItem("token");

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);

  const [notices, setNotices] = useState([]);
  const [loadingId, setLoadingId] = useState(null);
  const [adding, setAdding] = useState(false);

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    const res = await fetch(`${API}/api/notices`);
    const data = await res.json();
    setNotices(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= ADD ================= */
  const addNotice = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("‚ùå Login as admin first");
      return;
    }

    setAdding(true);

    try {
      const fd = new FormData();
      fd.append("title", title);
      fd.append("description", description);
      fd.append("isImportant", isImportant);
      if (file) fd.append("attachment", file);

      const res = await fetch(`${API}/api/notices`, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: fd,
      });

      const result = await res.json();

      if (!res.ok) {
        alert(result.message || "Failed to add notice");
        return;
      }

      setTitle("");
      setDescription("");
      setIsImportant(false);
      setFile(null);

      fetchNotices();
      alert("‚úÖ Notice added");
    } catch (err) {
      alert("‚ùå Server error");
    } finally {
      setAdding(false);
    }
  };

  /* ================= DELETE ================= */
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    setLoadingId(id);

    try {
      const res = await fetch(`${API}/api/notices/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const result = await res.json();

      if (!res.ok) {
        alert(result.message || "Delete failed");
        return;
      }

      fetchNotices();
      alert("üóëÔ∏è Notice deleted");
    } catch {
      alert("‚ùå Delete error");
    } finally {
      setLoadingId(null);
    }
  };

  return (
    <div className="manage-notices">
      <h2>Manage Notices</h2>

      {/* ADD */}
      <form onSubmit={addNotice} className="notice-form">
        <input
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label>
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          Important
        </label>

        <input
          type="file"
          accept=".jpg,.jpeg,.png,.pdf"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button type="submit" disabled={adding}>
          {adding ? "Adding..." : "Add Notice"}
        </button>
      </form>

      {/* LIST */}
      {notices.map((n) => (
        <div key={n._id} className="notice-card">
          <h4>{n.title}</h4>
          <p>{n.description}</p>

          {n.attachment && (
            <a href={n.attachment} target="_blank" rel="noreferrer">
              üìé View Attachment
            </a>
          )}

          <button
            onClick={() => deleteNotice(n._id)}
            disabled={loadingId === n._id}
          >
            {loadingId === n._id ? "Deleting..." : "Delete"}
          </button>
        </div>
      ))}
    </div>
  );
};

export default ManageNotices;
