import React, { useEffect, useState } from "react";
import "./ManageAbout.css";

const API = "https://backend-9i6n.onrender.com";

const emptyWard = {
  name: "",
  totalVoters: "",
  maleVoters: "",
  femaleVoters: "",
  houses: "",
};

const ManageAbout = () => {
  const token = localStorage.getItem("token");

  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    villageName: "",
    history: "",
    area: "",
    description: "",

    populationTotal: "",
    populationMale: "",
    populationFemale: "",
    literacyRate: "",

    busAvailable: "yes",
    busCount: "",
    schoolCount: "",
    schoolNames: "",

    wards: [
      { ...emptyWard },
      { ...emptyWard },
      { ...emptyWard },
      { ...emptyWard },
    ],
  });

  /* ================= FETCH ================= */
  useEffect(() => {
    fetch(`${API}/api/village`)
      .then((res) => res.json())
      .then((data) => {
        if (data) setForm(data);
      })
      .catch((err) => console.error(err));
  }, []);

  /* ================= CHANGE ================= */
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleWardChange = (index, e) => {
    const updated = [...form.wards];
    updated[index][e.target.name] = e.target.value;
    setForm({ ...form, wards: updated });
  };

  /* ================= SAVE ================= */
  const saveData = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API}/api/village`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });

      if (!res.ok) throw new Error();
      alert("‚úÖ Village information updated");
      setEditMode(false);
    } catch {
      alert("‚ùå Failed to save data");
    }
  };

  return (
    <div className="manage-about">
      <h2>üè° Village Information (Admin)</h2>

      {!editMode && (
        <div className="about-card">
          <h3>{form.villageName}</h3>
          <p>{form.history}</p>

          <h4>üìä Population</h4>
          <ul>
            <li>üë• Total: {form.populationTotal}</li>
            <li>üë® Male: {form.populationMale}</li>
            <li>üë© Female: {form.populationFemale}</li>
            <li>üìö Literacy Rate: {form.literacyRate}%</li>
          </ul>

          <h4>üèòÔ∏è Wards</h4>
          {form.wards.map((w, i) => (
            <div key={i} className="ward-view">
              <b>{w.name || `Ward ${i + 1}`}</b> ‚Äî
              Voters: {w.totalVoters},
              Male: {w.maleVoters},
              Female: {w.femaleVoters},
              Houses: {w.houses}
            </div>
          ))}

          <h4>üöå Facilities</h4>
          <p>Bus Service: {form.busAvailable}</p>
          <p>Buses per day: {form.busCount}</p>
          <p>Schools: {form.schoolCount}</p>
          <p>School Names: {form.schoolNames}</p>

          <button onClick={() => setEditMode(true)}>‚úèÔ∏è Edit</button>
        </div>
      )}

      {editMode && (
        <form className="about-form" onSubmit={saveData}>
          <h3>Basic Info</h3>
          <input name="villageName" placeholder="Village Name" value={form.villageName} onChange={handleChange} />
          <textarea name="history" placeholder="Village History" value={form.history} onChange={handleChange} />
          <input name="area" placeholder="Area (sq km)" value={form.area} onChange={handleChange} />
          <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} />

          <h3>Population</h3>
          <input name="populationTotal" placeholder="Total Population" value={form.populationTotal} onChange={handleChange} />
          <input name="populationMale" placeholder="Male Population" value={form.populationMale} onChange={handleChange} />
          <input name="populationFemale" placeholder="Female Population" value={form.populationFemale} onChange={handleChange} />
          <input name="literacyRate" placeholder="Literacy Rate (%)" value={form.literacyRate} onChange={handleChange} />

          <h3>Wards</h3>
          {form.wards.map((w, i) => (
            <div key={i} className="ward-box">
              <h4>Ward {i + 1}</h4>
              <input name="name" placeholder="Ward Name" value={w.name} onChange={(e) => handleWardChange(i, e)} />
              <input name="totalVoters" placeholder="Total Voters" value={w.totalVoters} onChange={(e) => handleWardChange(i, e)} />
              <input name="maleVoters" placeholder="Male Voters" value={w.maleVoters} onChange={(e) => handleWardChange(i, e)} />
              <input name="femaleVoters" placeholder="Female Voters" value={w.femaleVoters} onChange={(e) => handleWardChange(i, e)} />
              <input name="houses" placeholder="Houses" value={w.houses} onChange={(e) => handleWardChange(i, e)} />
            </div>
          ))}

          <h3>Facilities</h3>
          <select name="busAvailable" value={form.busAvailable} onChange={handleChange}>
            <option value="yes">Bus Available</option>
            <option value="no">No Bus</option>
          </select>

          <input name="busCount" placeholder="Buses per day" value={form.busCount} onChange={handleChange} />
          <input name="schoolCount" placeholder="Number of Schools" value={form.schoolCount} onChange={handleChange} />
          <textarea name="schoolNames" placeholder="School Names" value={form.schoolNames} onChange={handleChange} />

          <div className="form-actions">
            <button type="submit">üíæ Save</button>
            <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ManageAbout;
