import React, { useState, useEffect, useCallback } from "react";

// The base URL for your API
const API_URL = "http://localhost:5000/api/notices";

const ManageNotices = () => {
  const [noticeText, setNoticeText] = useState(""); // Holds text for the new notice
  const [notices, setNotices] = useState([]);       // Holds list of existing notices from DB
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // --- READ: Fetch Notices from the DB ---
  const fetchNotices = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      // NOTE: You might need to include authentication headers here for protected routes
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error("Failed to fetch notices.");
      }
      const data = await response.json();
      setNotices(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchNotices();
  }, [fetchNotices]);

  // --- CREATE: Add a New Notice ---
  const addNotice = async (e) => {
    e.preventDefault();
    if (!noticeText.trim()) return;

    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          // Include Admin's JWT token for authorization
          // "Authorization": `Bearer ${adminToken}`, 
        },
        body: JSON.stringify({ content: noticeText.trim() }), // Send the notice content
      });

      if (!response.ok) {
        throw new Error("Failed to add notice.");
      }

      // After successful creation, fetch the updated list from the DB
      await fetchNotices();
      setNoticeText(""); // Clear the input field
    } catch (err) {
      setError(err.message);
    }
  };

  // --- DELETE: Remove a Notice ---
  const deleteNotice = async (id) => {
    try {
      const response = await fetch(`${API_URL}/${id}`, {
        method: "DELETE",
        headers: {
          // Include Admin's JWT token for authorization
          // "Authorization": `Bearer ${adminToken}`, 
        },
      });

      if (!response.ok) {
        throw new Error("Failed to delete notice.");
      }

      // Update the state locally for a faster UI update
      setNotices(notices.filter(notice => notice._id !== id));
      
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <p>Loading notices...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;

  return (
    <div className="manage-notices-container">
      <h2>Manage Notices</h2>

      {/* Notice Creation Form */}
      <form onSubmit={addNotice} className="notice-form">
        <textarea
          placeholder="Enter notice content..."
          value={noticeText}
          onChange={(e) => setNoticeText(e.target.value)}
          rows="4"
        />
        <button type="submit">Publish Notice</button>
      </form>

      {/* List of Existing Notices (Read) */}
      <h3>Current Public Notices</h3>
      <ul className="notices-list">
        {notices.length === 0 ? (
          <p>No notices currently published.</p>
        ) : (
          notices.map((n) => (
            <li key={n._id} className="notice-item">
              <p>{n.content}</p>
              <span className="notice-date">
                  Published: {new Date(n.createdAt).toLocaleDateString()}
              </span>
              <button 
                onClick={() => deleteNotice(n._id)}
                className="delete-button"
              >
                Delete
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default ManageNotices;
