import React, { useEffect, useState } from "react";
import "./Notices.css";

const API =
  process.env.REACT_APP_API_URL || "https://backend-9i6n.onrender.com";

const Notices = () => {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const [openingId, setOpeningId] = useState(null); // ‚úÖ NEW

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await fetch(`${API}/api/notices`);
        if (!res.ok) throw new Error("Failed to fetch notices");

        const data = await res.json();

        if (Array.isArray(data)) {
          const sorted = data.sort((a, b) => {
            if (a.isImportant === b.isImportant) {
              return new Date(b.createdAt) - new Date(a.createdAt);
            }
            return b.isImportant - a.isImportant;
          });
          setNotices(sorted);
        } else {
          setNotices([]);
        }
      } catch (err) {
        console.error("Notice fetch error:", err);
        setError("Unable to load notices");
      } finally {
        setLoading(false);
      }
    };

    fetchNotices();
  }, []);

  const openAttachment = (id, url) => {
    setOpeningId(id);
    setTimeout(() => {
      window.open(url, "_blank");
      setOpeningId(null);
    }, 500); // small UX delay
  };

  if (loading) {
    return (
      <div className="notice-container">
        <div className="loading-wrapper">
          <div className="spinner"></div>
          <p className="loading-text">Loading notices...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="notice-container">
        <div className="error-box">
          <p className="error-text">‚ö†Ô∏è {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="notice-container">
      <div className="notice-content">
        <div className="notice-header">
          <h1 className="header-title">Latest Notice</h1>
          <p className="header-subtitle">
            Stay updated with the latest announcements
          </p>
        </div>

        {notices.length === 0 ? (
          <div className="empty-state">
            <p className="empty-text">No notices available</p>
          </div>
        ) : (
          <div className="notice-grid">
            {notices.map((notice) => (
              <div
                key={notice._id}
                className={`notice-card ${
                  notice.isImportant ? "notice-important" : ""
                }`}
              >
                {notice.isImportant && (
                  <div className="important-ribbon">
                    <span>IMPORTANT</span>
                  </div>
                )}

                <div className="notice-body">
                  <h3 className="notice-title">{notice.title}</h3>

                  <p className="notice-date">
                    {new Date(notice.createdAt).toLocaleDateString()}
                  </p>

                  <p className="notice-description">{notice.description}</p>

                  {/* ‚úÖ IMAGE OR DOCUMENT WITH LOADER */}
                  {notice.attachment && (() => {
  const fileUrl = notice.attachment;
  const isImage = /\.(jpg|jpeg|png|webp)$/i.test(fileUrl);
  const isPdf = /\.pdf$/i.test(fileUrl);

  return (
    <div className="attachment-box">
      {isImage ? (
        <a href={fileUrl} target="_blank" rel="noopener noreferrer">
          <img
            src={fileUrl}
            alt="Notice Attachment"
            className="notice-image"
          />
        </a>
      ) : isPdf ? (
        <a
          href={fileUrl}
          target="_blank"
          rel="noopener noreferrer"
          className="doc-link"
        >
          üìÑ View PDF Document
        </a>
      ) : (
        <a
          href={fileUrl}
          target="_blank"
          rel="noopener noreferrer"
          className="doc-link"
        >
          üìé View Attachment
        </a>
      )}
    </div>
  );
})()}

                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Notices;
