import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useLocation } from "react-router-dom";
import "./Service.css";

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const query = new URLSearchParams(location.search);
  const selectedService = query.get("service");

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",

    // ðŸ”´ Death certificate specific
    deceasedName: "",
    deceasedAge: "",
    dateOfDeath: "",
    placeOfDeath: "",
    applicantRelation: ""
  });

  const [documents, setDocuments] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (selectedService) {
      setFormData((prev) => ({
        ...prev,
        serviceType: selectedService
      }));
    }
  }, [selectedService]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFiles = (e) => {
    setDocuments(e.target.files);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();

    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    for (let i = 0; i < documents.length; i++) {
      data.append("documents", documents[i]);
    }

    try {
      const res = await axios.post(
        "http://localhost:5000/api/services/apply",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data"
          }
        }
      );

      setMessage(res.data.message || "Application submitted successfully");
    } catch (err) {
      setMessage(err.response?.data?.message || "Failed to submit");
    }
  };

  return (
    <div className="apply-service-container">
      <h2>Apply for Service</h2>

      {message && <p className="msg">{message}</p>}

      <form onSubmit={handleSubmit}>
        {/* SERVICE */}
        <label>Service</label>
        <select
          name="serviceType"
          value={formData.serviceType}
          onChange={handleChange}
          disabled={!!selectedService}
          required
        >
          <option value="">Select Service</option>
          <option value="Birth Certificate">Birth Certificate</option>
          <option value="Death Certificate">Death Certificate</option>
          <option value="Income Certificate">Income Certificate</option>
          <option value="Sanitation">Sanitation</option>
        </select>

        {/* APPLICANT DETAILS */}
        <h4>Applicant Details</h4>
        <input
          type="text"
          name="fullName"
          placeholder="Applicant Full Name"
          value={formData.fullName}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="address"
          placeholder="Applicant Address"
          value={formData.address}
          onChange={handleChange}
          required
        />

        <input
          type="text"
          name="mobile"
          placeholder="Applicant Mobile Number"
          value={formData.mobile}
          onChange={handleChange}
          required
        />

        {/* ðŸ”´ DEATH CERTIFICATE EXTRA FIELDS */}
        {formData.serviceType === "Death Certificate" && (
          <>
            <h4>Deceased Person Details</h4>

            <input
              type="text"
              name="deceasedName"
              placeholder="Deceased Person Name"
              value={formData.deceasedName}
              onChange={handleChange}
              required
            />

            <input
              type="number"
              name="deceasedAge"
              placeholder="Age at Death"
              value={formData.deceasedAge}
              onChange={handleChange}
              required
            />

            <input
              type="date"
              name="dateOfDeath"
              value={formData.dateOfDeath}
              onChange={handleChange}
              required
            />

            <input
              type="text"
              name="placeOfDeath"
              placeholder="Place of Death"
              value={formData.placeOfDeath}
              onChange={handleChange}
              required
            />

            <input
              type="text"
              name="applicantRelation"
              placeholder="Relation with Deceased (Son/Daughter/Wife)"
              value={formData.applicantRelation}
              onChange={handleChange}
              required
            />

            {/* REQUIRED DOCS INFO */}
            <div className="required-docs">
              <h4>Required Documents</h4>
              <ul>
                <li>ðŸ“„ Hospital Death Slip</li>
                <li>ðŸ“„ Aadhaar of Deceased</li>
                <li>ðŸ“„ Aadhaar of Applicant</li>
                <li>ðŸ“„ Address Proof</li>
              </ul>
            </div>
          </>
        )}

        {/* DOCUMENT UPLOAD */}
        <label>Upload Documents</label>
        <input type="file" multiple onChange={handleFiles} required />

        <button type="submit">Submit Application</button>
      </form>
    </div>
  );
};

export default ApplyService;
