import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./UserProfile.css";

const UserProfile = () => {
  const { token, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const res = await axios.get("http://localhost:5000/api/user/profile", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setProfile(res.data);
      setError(null);
    } catch (err) {
      console.error("Failed to load profile", err);
      setError("Failed to load profile. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, [token]);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  // üîÅ RE-APPLY
  const handleReapply = (serviceType) => {
    navigate(`/apply-service?service=${encodeURIComponent(serviceType)}`);
  };

  // ‚ùå CANCEL APPLICATION
  const handleCancel = async (id) => {
    if (!window.confirm("Are you sure you want to cancel this application?")) {
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/services/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProfile();
    } catch (err) {
      alert("Failed to cancel application. Please try again.");
    }
  };

  // Get status badge class and icon
  const getStatusBadge = (status) => {
    const badges = {
      Pending: { icon: "‚è≥", class: "pending", label: "Pending" },
      Approved: { icon: "‚úÖ", class: "approved", label: "Approved" },
      Rejected: { icon: "‚ùå", class: "rejected", label: "Rejected" },
      Completed: { icon: "üéâ", class: "completed", label: "Completed" },
    };
    return badges[status] || { icon: "üìã", class: "default", label: status };
  };

  // Get statistics
  const getStats = () => {
    if (!profile || !profile.services) return { total: 0, pending: 0, approved: 0, rejected: 0 };
    
    return {
      total: profile.services.length,
      pending: profile.services.filter(s => s.status === "Pending").length,
      approved: profile.services.filter(s => s.status === "Approved").length,
      rejected: profile.services.filter(s => s.status === "Rejected").length,
    };
  };

  if (loading) {
    return (
      <div className="profile-container">
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Loading your profile...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-container">
        <div className="error-state">
          <span className="error-icon">‚ö†Ô∏è</span>
          <p>{error}</p>
          <button className="retry-btn" onClick={fetchProfile}>
            Retry
          </button>
        </div>
      </div>
    );
  }

  if (!profile) return null;

  const { user, services } = profile;
  const stats = getStats();

  return (
    <div className="profile-container">
      {/* Header Section */}
      <div className="profile-header">
        <div className="header-content">
          <div className="user-avatar">
            <span className="avatar-text">
              {user.fullName.charAt(0).toUpperCase()}
            </span>
          </div>
          <div className="header-info">
            <h1 className="profile-title">Welcome, {user.fullName}</h1>
            <p className="profile-subtitle">Manage your profile and applications</p>
          </div>
        </div>
        <button className="logout-btn" onClick={handleLogout}>
          <span className="btn-icon">üö™</span>
          Logout
        </button>
      </div>

      {/* Statistics Cards */}
      <div className="stats-grid">
        <div className="stat-card total">
          <div className="stat-icon">üìä</div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.total}</h3>
            <p className="stat-label">Total Applications</p>
          </div>
        </div>
        <div className="stat-card pending">
          <div className="stat-icon">‚è≥</div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.pending}</h3>
            <p className="stat-label">Pending</p>
          </div>
        </div>
        <div className="stat-card approved">
          <div className="stat-icon">‚úÖ</div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.approved}</h3>
            <p className="stat-label">Approved</p>
          </div>
        </div>
        <div className="stat-card rejected">
          <div className="stat-icon">‚ùå</div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.rejected}</h3>
            <p className="stat-label">Rejected</p>
          </div>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="profile-grid">
        {/* Basic Information Card */}
        <div className="profile-card info-card">
          <div className="card-header">
            <h3 className="card-title">
              <span className="title-icon">üë§</span>
              Personal Information
            </h3>
          </div>
          <div className="card-body">
            <div className="info-row">
              <span className="info-label">
                <span className="label-icon">üìù</span>
                Full Name
              </span>
              <span className="info-value">{user.fullName}</span>
            </div>
            <div className="info-row">
              <span className="info-label">
                <span className="label-icon">‚úâÔ∏è</span>
                Email Address
              </span>
              <span className="info-value">{user.email}</span>
            </div>
            <div className="info-row">
              <span className="info-label">
                <span className="label-icon">üèòÔ∏è</span>
                Village
              </span>
              <span className="info-value">{user.village}</span>
            </div>
            <div className="info-row">
              <span className="info-label">
                <span className="label-icon">üì±</span>
                Mobile Number
              </span>
              <span className="info-value">{user.mobile}</span>
            </div>
          </div>
        </div>

        {/* Application History Card */}
        <div className="profile-card applications-card">
          <div className="card-header">
            <h3 className="card-title">
              <span className="title-icon">üìã</span>
              Application History
            </h3>
            <button 
              className="apply-new-btn"
              onClick={() => navigate('/apply-service')}
            >
              + New Application
            </button>
          </div>
          <div className="card-body">
            {services.length === 0 ? (
              <div className="empty-state">
                <span className="empty-icon">üì≠</span>
                <p className="empty-text">No applications yet</p>
                <button 
                  className="start-btn"
                  onClick={() => navigate('/apply-service')}
                >
                  Apply for Service
                </button>
              </div>
            ) : (
              <div className="services-list">
                {services.map((s) => {
                  const badge = getStatusBadge(s.status);
                  return (
                    <div
                      key={s._id}
                      className={`service-item status-${badge.class}`}
                    >
                      <div className="service-header">
                        <div className="service-info">
                          <h4 className="service-name">{s.serviceType}</h4>
                          <span className={`status-badge ${badge.class}`}>
                            <span className="badge-icon">{badge.icon}</span>
                            {badge.label}
                          </span>
                        </div>
                        <p className="service-date">
                          {new Date(s.createdAt).toLocaleDateString("en-IN", {
                            day: "2-digit",
                            month: "short",
                            year: "numeric",
                          })}
                        </p>
                      </div>

                      {/* Rejected with Remark */}
                      {s.status === "Rejected" && s.adminRemark && (
                        <div className="rejection-notice">
                          <p className="rejection-label">
                            <span className="notice-icon">üí¨</span>
                            Admin Remark:
                          </p>
                          <p className="rejection-text">{s.adminRemark}</p>
                        </div>
                      )}

                      {/* Action Buttons */}
                      <div className="service-actions">
                        {s.status === "Rejected" && (
                          <button
                            className="action-btn reapply-btn"
                            onClick={() => handleReapply(s.serviceType)}
                          >
                            <span className="btn-icon">üîÅ</span>
                            Re-Apply
                          </button>
                        )}

                        {s.status === "Pending" && (
                          <button
                            className="action-btn cancel-btn"
                            onClick={() => handleCancel(s._id)}
                          >
                            <span className="btn-icon">üóëÔ∏è</span>
                            Cancel
                          </button>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
