import React, { useEffect, useState } from "react";
import "./UserNotices.css";

const UserNotices = () => {
  const [notices, setNotices] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await fetch(
          `${process.env.REACT_APP_API_URL}/api/notices`
        );

        if (!res.ok) {
          throw new Error("Failed to fetch notices");
        }

        const data = await res.json();
        setNotices(data);
      } catch (err) {
        console.error(err);
        setError("Unable to load notices");
      }
    };

    fetchNotices();
  }, []);

  if (error) {
    return <p className="loading-text">{error}</p>;
  }

  return (
    <div className="user-notice-section">
      <h2>Latest Notices & Announcements</h2>

      {notices.length === 0 ? (
        <p className="loading-text">No notices available</p>
      ) : (
        <div className="user-notice-list">
          {notices.map((notice) => (
            <div
              key={notice._id}
              className={`user-notice-card ${
                notice.isImportant ? "important" : ""
              }`}
            >
              <h3>{notice.title}</h3>
              <p>{notice.description}</p>

              {notice.isImportant && (
                <span className="important-badge">Important</span>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserNotices;
