import React, { useEffect, useState } from "react";
import "./ManageDevelopment.css";

const ManageDevelopment = () => {
  const [projects, setProjects] = useState([]);
  const [images, setImages] = useState([]);
  const [form, setForm] = useState({
    projectName: "",
    description: "",
    progress: 0,
    fundsUsed: "",
  });

  const token = localStorage.getItem("token");

  // FETCH PROJECTS ON LOAD
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    const res = await fetch("http://localhost:5000/api/development");
    const data = await res.json();
    setProjects(data);
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // ADD PROJECT
  const addProject = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("projectName", form.projectName);
    formData.append("description", form.description);
    formData.append("progress", form.progress);
    formData.append("fundsUsed", form.fundsUsed);

    for (let i = 0; i < images.length; i++) {
      formData.append("images", images[i]);
    }

    await fetch("http://localhost:5000/api/development", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    // RESET FORM
    setForm({
      projectName: "",
      description: "",
      progress: 0,
      fundsUsed: "",
    });
    setImages([]);

    // REFRESH LIST
    fetchProjects();
  };

  return (
    <div className="manage-development">
      <h2>Village Development Projects</h2>

      {/* ADD FORM */}
      <form className="dev-form" onSubmit={addProject}>
        <input
          name="projectName"
          placeholder="Project Name"
          value={form.projectName}
          onChange={handleChange}
          required
        />

        <textarea
          name="description"
          placeholder="Description"
          value={form.description}
          onChange={handleChange}
        />

        <input
          name="fundsUsed"
          placeholder="Funds Used (₹)"
          value={form.fundsUsed}
          onChange={handleChange}
        />

        <input
          type="number"
          name="progress"
          placeholder="Progress %"
          value={form.progress}
          onChange={handleChange}
          min="0"
          max="100"
          required
        />

        <input
          type="file"
          multiple
          onChange={(e) => setImages(e.target.files)}
        />

        <button type="submit">Add Project</button>
      </form>

      {/* PROJECT LIST */}
      <div className="dev-list">
        {projects.map((p) => (
          <div className="dev-card" key={p._id}>
            <h4>{p.projectName}</h4>
            <p>{p.description}</p>

            {/* STATUS */}
            <span
              className={`dev-status ${
                p.status === "Completed" ? "completed" : "ongoing"
              }`}
            >
              {p.status}
            </span>

            {/* PROGRESS BAR */}
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{ width: `${p.progress}%` }}
              ></div>
            </div>

            <p><b>Progress:</b> {p.progress}%</p>
            <p><b>Funds Used:</b> ₹{p.fundsUsed}</p>

            {/* IMAGES */}
            {p.images?.length > 0 && (
              <div className="dev-images">
                {p.images.map((img, i) => (
                  <img
                    key={i}
                    src={`http://localhost:5000${img}`}
                    alt="Development"
                  />
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageDevelopment;
