import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useLocation } from "react-router-dom";
import "./ApplyService.css";

const API =
  process.env.REACT_APP_API_URL || "https://backend-9i6n.onrender.com";

// ‚úÖ MAX FILE SIZE = 500 KB
const MAX_FILE_SIZE = 500 * 1024;

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",
    deceasedName: "",
    dateOfDeath: "",
  });

  const [files, setFiles] = useState({});
  const [fileErrors, setFileErrors] = useState({});
  const [loading, setLoading] = useState(false);

  /* ================= PREFILL SERVICE ================= */
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const serviceFromUrl = params.get("service");

    if (serviceFromUrl) {
      setFormData((prev) => ({
        ...prev,
        serviceType: serviceFromUrl,
      }));
    }
  }, [location.search]);

  /* ================= INPUT ================= */
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /* ================= FILE VALIDATION ================= */
  const handleFileChange = (e) => {
    const { name, files: selectedFiles } = e.target;
    const file = selectedFiles[0];

    if (!file) return;

    const allowedTypes = [
      "image/jpeg",
      "image/png",
      "image/jpg",
      "application/pdf",
    ];

    // ‚ùå Invalid type
    if (!allowedTypes.includes(file.type)) {
      setFileErrors((prev) => ({
        ...prev,
        [name]: "‡§´‡§ï‡•ç‡§§ JPG, PNG ‡§ï‡§ø‡§Ç‡§µ‡§æ PDF ‡§´‡§æ‡§à‡§≤ ‡§Ö‡§™‡§≤‡•ã‡§° ‡§ï‡§∞‡§æ",
      }));
      e.target.value = "";
      return;
    }

    // ‚ùå Size > 500 KB
    if (file.size > MAX_FILE_SIZE) {
      setFileErrors((prev) => ({
        ...prev,
        [name]: "‡§´‡§æ‡§à‡§≤‡§ö‡§æ ‡§Ü‡§ï‡§æ‡§∞ 500 KB ‡§™‡•á‡§ï‡•ç‡§∑‡§æ ‡§ú‡§æ‡§∏‡•ç‡§§ ‡§®‡§∏‡§æ‡§µ‡§æ",
      }));
      e.target.value = "";
      return;
    }

    // ‚úÖ Valid file
    setFileErrors((prev) => ({ ...prev, [name]: "" }));
    setFiles((prev) => ({ ...prev, [name]: file }));
  };

  /* ================= SUBMIT ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const data = new FormData();
    Object.entries(formData).forEach(([k, v]) => data.append(k, v));
    Object.entries(files).forEach(([k, f]) => data.append(k, f));

    try {
      await axios.post(`${API}/api/services/apply`, data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("‡§Ö‡§∞‡•ç‡§ú ‡§Ø‡§∂‡§∏‡•ç‡§µ‡•Ä‡§∞‡•Ä‡§§‡•ç‡§Ø‡§æ ‡§∏‡§æ‡§¶‡§∞ ‡§ù‡§æ‡§≤‡§æ ‚úÖ");

      setFormData({
        serviceType: "",
        fullName: "",
        address: "",
        mobile: "",
        deceasedName: "",
        dateOfDeath: "",
      });
      setFiles({});
      setFileErrors({});
    } catch (err) {
      alert("‡§Ö‡§∞‡•ç‡§ú ‡§∏‡§æ‡§¶‡§∞ ‡§π‡•ã‡§ä ‡§∂‡§ï‡§≤‡§æ ‡§®‡§æ‡§π‡•Ä ‚ùå");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-service-container">
      <h2>‡§∏‡•á‡§µ‡•á‡§∏‡§æ‡§†‡•Ä ‡§Ö‡§∞‡•ç‡§ú ‡§ï‡§∞‡§æ</h2>

      <p className="file-help">
        üìå ‡§∏‡§∞‡•ç‡§µ ‡§ï‡§æ‡§ó‡§¶‡§™‡§§‡•ç‡§∞‡§æ‡§Ç‡§ö‡•Ä ‡§´‡§æ‡§à‡§≤ ‡§∏‡§æ‡§à‡§ú <b>500 KB ‡§™‡•á‡§ï‡•ç‡§∑‡§æ ‡§ï‡§Æ‡•Ä</b> ‡§Ö‡§∏‡§æ‡§µ‡•Ä <br />
        üìâ ‡§´‡§æ‡§à‡§≤ compress ‡§ï‡§∞‡§£‡•ç‡§Ø‡§æ‡§∏‡§æ‡§†‡•Ä:{" "}
        <a
          href="https://www.ilovepdf.com/compress_image"
          target="_blank"
          rel="noreferrer"
        >
          ilovepdf.com
        </a>
      </p>

      <form onSubmit={handleSubmit} encType="multipart/form-data">
        {/* SERVICE */}
        <label>‡§∏‡•á‡§µ‡§æ ‡§®‡§ø‡§µ‡§°‡§æ</label>
        <select
          name="serviceType"
          value={formData.serviceType}
          onChange={handleChange}
          required
        >
          <option value="">-- ‡§∏‡•á‡§µ‡§æ ‡§®‡§ø‡§µ‡§°‡§æ --</option>
          <option value="‡§ú‡§®‡•ç‡§Æ ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§ú‡§®‡•ç‡§Æ ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§Æ‡•É‡§§‡•ç‡§Ø‡•Ç ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§Æ‡•É‡§§‡•ç‡§Ø‡•Ç ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§â‡§§‡•ç‡§™‡§®‡•ç‡§® ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§â‡§§‡•ç‡§™‡§®‡•ç‡§® ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§∞‡§π‡§ø‡§µ‡§æ‡§∏‡•Ä ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§∞‡§π‡§ø‡§µ‡§æ‡§∏‡•Ä ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§µ‡§ø‡§µ‡§æ‡§π ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§µ‡§ø‡§µ‡§æ‡§π ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
        </select>

        {/* APPLICANT */}
        <h4>‡§Ö‡§∞‡•ç‡§ú‡§¶‡§æ‡§∞‡§æ‡§ö‡•Ä ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä</h4>
        <input name="fullName" placeholder="‡§™‡•Ç‡§∞‡•ç‡§£ ‡§®‡§æ‡§µ" required onChange={handleChange} />
        <input name="address" placeholder="‡§™‡§§‡•ç‡§§‡§æ" required onChange={handleChange} />
        <input name="mobile" placeholder="‡§Æ‡•ã‡§¨‡§æ‡§à‡§≤ ‡§ï‡•ç‡§∞‡§Æ‡§æ‡§Ç‡§ï" required onChange={handleChange} />

        {/* BIRTH */}
