import React, { useEffect, useState } from "react";
import "./ManageContacts.css";

const ManageContacts = () => {
  const token = localStorage.getItem("token");

  // ✅ TOKEN CHECK MUST BE INSIDE COMPONENT
  if (!token) {
    return <p>Please login as admin</p>;
  }

  const [members, setMembers] = useState([]);
  const [selectedMember, setSelectedMember] = useState(null);
  const [editMode, setEditMode] = useState(false);

  // form states
  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [photo, setPhoto] = useState(null);

  // FETCH MEMBERS (PUBLIC)
  const fetchMembers = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/contacts");
      const data = await res.json();
      setMembers(data);
    } catch (err) {
      console.error("Fetch failed", err);
    }
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  // ADD MEMBER (ADMIN)
  const addMember = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("role", role);
    formData.append("phone", phone);
    formData.append("email", email);
    if (photo) formData.append("photo", photo);

    try {
      await fetch("http://localhost:5000/api/contacts", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`, // ✅ TOKEN REQUIRED
        },
        body: formData,
      });

      setName("");
      setRole("");
      setPhone("");
      setEmail("");
      setPhoto(null);

      fetchMembers();
    } catch (err) {
      console.error("Add member failed", err);
    }
  };

  // DELETE MEMBER (ADMIN)
  const deleteMember = async (id) => {
    if (!window.confirm("Are you sure?")) return;

    await fetch(`http://localhost:5000/api/contacts/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`, // ✅ TOKEN REQUIRED
      },
    });

    setSelectedMember(null);
    fetchMembers();
  };

  // UPDATE MEMBER (ADMIN)
  const updateMember = async () => {
    await fetch(`http://localhost:5000/api/contacts/${selectedMember._id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`, // ✅ TOKEN REQUIRED
      },
      body: JSON.stringify({
        name: selectedMember.name,
        role: selectedMember.role,
        phone: selectedMember.phone,
        email: selectedMember.email,
      }),
    });

    setEditMode(false);
    fetchMembers();
  };

  return (
    <div className="manage-container">
      <h2>Manage Gram Panchayat Members</h2>

      {/* ADD MEMBER FORM */}
      <form className="member-form" onSubmit={addMember}>
        <input
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          placeholder="Role"
          value={role}
          onChange={(e) => setRole(e.target.value)}
          required
        />
        <input
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
        <input
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input type="file" onChange={(e) => setPhoto(e.target.files[0])} />
        <button type="submit">Add Member</button>
      </form>

      {/* CARD GRID */}
      <div className="member-grid">
        {members.map((m) => (
          <div className="member-card" key={m._id}>
            <img src={`http://localhost:5000${m.photo}`} alt={m.name} />
            <h4>{m.name}</h4>
            <p>{m.role}</p>
            <button className="view-btn" onClick={() => setSelectedMember(m)}>
              View
            </button>
          </div>
        ))}
      </div>

      {/* VIEW / EDIT MODAL */}
      {selectedMember && (
        <div className="modal">
          <div className="modal-content">
            <h3>Member Details</h3>

            <img
              src={`http://localhost:5000${selectedMember.photo}`}
              alt=""
              className="modal-img"
            />

            <label>Name</label>
            <input
              value={selectedMember.name}
              disabled={!editMode}
              onChange={(e) =>
                setSelectedMember({ ...selectedMember, name: e.target.value })
              }
            />
      
            <label>Role</label>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              required
            >
              <option value="">Select Role</option>
              <option value="Sarpanch">Sarpanch</option>
              <option value="Upa-Sarpanch">Upa-Sarpanch</option>
              <option value="Gram Sevak">Gram Sevak</option>
              <option value="Panchayat Member">Panchayat Member</option>
              <option value="Talathi">Talathi</option>
              <option value="Village Development Officer">
                Village Development Officer (VDO)
              </option>
            </select>

            <label>Phone</label>
            <input
              value={selectedMember.phone}
              disabled={!editMode}
              onChange={(e) =>
                setSelectedMember({ ...selectedMember, phone: e.target.value })
              }
            />

            <label>Email</label>
            <input
              value={selectedMember.email}
              disabled={!editMode}
              onChange={(e) =>
                setSelectedMember({ ...selectedMember, email: e.target.value })
              }
            />

            <div className="modal-actions">
              {!editMode ? (
                <button onClick={() => setEditMode(true)}>Edit</button>
              ) : (
                <button onClick={updateMember}>Save</button>
              )}

              <button
                className="delete-btn"
                onClick={() => deleteMember(selectedMember._id)}
              >
                Delete
              </button>

              <button
                className="close-btn"
                onClick={() => {
                  setSelectedMember(null);
                  setEditMode(false);
                }}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageContacts;
