import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5MB

const ManageNotices = () => {
  const token = localStorage.getItem("token");

  /* ================= STATES ================= */
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);

  const [notices, setNotices] = useState([]);

  const [uploadProgress, setUploadProgress] = useState(0);
  const [filePreview, setFilePreview] = useState(null);
  const [fileError, setFileError] = useState("");

  const [popup, setPopup] = useState({ show: false, message: "", type: "" });

  /* ================= POPUP ================= */
  const showPopup = (message, type = "success") => {
    setPopup({ show: true, message, type });
    setTimeout(() => {
      setPopup({ show: false, message: "", type: "" });
    }, 3000);
  };

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    try {
      const res = await fetch(`${API}/api/notices`);
      const data = await res.json();
      setNotices(Array.isArray(data) ? data : []);
    } catch {
      showPopup("Failed to fetch notices", "error");
    }
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= FILE PROCESS ================= */
  const handleFile = (selectedFile) => {
    if (!selectedFile) return;

    const allowed = [
      "image/jpeg",
      "image/png",
      "image/jpg",
      "application/pdf",
    ];

    if (!allowed.includes(selectedFile.type)) {
      setFileError("âŒ Only JPG, PNG or PDF allowed");
      return;
    }

    if (selectedFile.size > MAX_FILE_SIZE) {
      setFileError("âš ï¸ File size must be under 5MB");
      return;
    }

    setFileError("");
    setFile(selectedFile);

    if (selectedFile.type === "application/pdf") {
      setFilePreview({ type: "pdf", url: URL.createObjectURL(selectedFile) });
    } else {
      setFilePreview({ type: "image", url: URL.createObjectURL(selectedFile) });
    }
  };

  /* ================= ADD NOTICE WITH PROGRESS ================= */
  const addNotice = (e) => {
    e.preventDefault();
    setUploadProgress(0);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("isImportant", isImportant);
    if (file) formData.append("attachment", file);

    const xhr = new XMLHttpRequest();
    xhr.open("POST", `${API}/api/notices`, true);
    xhr.setRequestHeader("Authorization", `Bearer ${token}`);

    xhr.upload.onprogress = (e) => {
      if (e.lengthComputable) {
        setUploadProgress(Math.round((e.loaded / e.total) * 100));
      }
    };

    xhr.onload = () => {
      if (xhr.status === 201 || xhr.status === 200) {
        showPopup("ðŸŽ‰ Notice uploaded successfully");
        setTitle("");
        setDescription("");
        setIsImportant(false);
        setFile(null);
        setFilePreview(null);
        setUploadProgress(0);
        fetchNotices();
      } else {
        showPopup("Upload failed", "error");
      }
    };

    xhr.onerror = () => {
      showPopup("Upload error", "error");
    };

    xhr.send(formData);
  };

  return (
    <div className="manage-notices">

      {/* POPUP */}
      {popup.show && (
        <div className="popup-overlay">
          <div className={`popup ${popup.type}`}>
            <p>{popup.message}</p>
          </div>
        </div>
      )}

      <h2>ðŸ“‹ Manage Notices</h2>

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label className="checkbox-label">
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          Important
        </label>

        {/* DRAG DROP */}
        <div
          className="drop-zone"
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => {
            e.preventDefault();
            handleFile(e.dataTransfer.files[0]);
          }}
        >
          <p>ðŸ“¥ Drag & drop file or click</p>
          <input
            type="file"
            accept=".pdf,.jpg,.jpeg,.png"
            onChange={(e) => handleFile(e.target.files[0])}
          />
        </div>

        {fileError && <p className="file-error">{fileError}</p>}

        {/* PREVIEW */}
        {filePreview && (
          <div className="file-preview">
            {filePreview.type === "image" ? (
              <img src={filePreview.url} alt="Preview" />
            ) : (
              <iframe src={filePreview.url} title="PDF Preview" />
            )}
          </div>
        )}

        {/* PROGRESS BAR */}
        {uploadProgress > 0 && (
          <div className="progress-wrapper">
            <div
              className="progress-bar"
              style={{ width: `${uploadProgress}%` }}
            >
              {uploadProgress}%
            </div>
          </div>
        )}

        <button type="submit" className="btn btn-primary">
          Upload Notice
        </button>
      </form>

      {/* LIST */}
      <div className="notice-list">
        {notices.map((n) => (
          <div key={n._id} className="notice-card">
            <h4>{n.title}</h4>
            <p>{n.description}</p>

            {n.attachment && (
              <a
                href={n.attachment}
                target="_blank"
                rel="noopener noreferrer"
              >
                ðŸ“Ž View / Download
              </a>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageNotices;
