import React, { useEffect, useState } from "react";
import "./ManageServices.css";

const API = "https://backend-9i6n.onrender.com";

const ManageServices = () => {
  const [applications, setApplications] = useState([]);
  const [activeFilter, setActiveFilter] = useState("Pending");

  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(""); // "Approved" | "Rejected"
  const [message, setMessage] = useState("");
  const [selectedAppId, setSelectedAppId] = useState(null);

  const token = localStorage.getItem("token");

  /* ================= FETCH ================= */
  const fetchApplications = async () => {
    const res = await fetch(`${API}/api/admin/services`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setApplications(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  /* ================= OPEN MODAL ================= */
  const openModal = (id, type) => {
    setSelectedAppId(id);
    setModalType(type);
    setMessage("");
    setShowModal(true);
  };

  /* ================= CONFIRM ================= */
  const confirmAction = async () => {
    if (!message.trim()) {
      alert("Message is required");
      return;
    }

    await fetch(`${API}/api/admin/services/${selectedAppId}/status`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        status: modalType,
        adminRemark: message,
      }),
    });

    setShowModal(false);
    setSelectedAppId(null);
    setMessage("");
    fetchApplications();
  };

  /* ================= FILTERS ================= */
  const pending = applications.filter((a) => a.status === "Pending");
  const approved = applications.filter((a) => a.status === "Approved");
  const rejected = applications.filter((a) => a.status === "Rejected");

  const filteredApps =
    activeFilter === "Approved"
      ? approved
      : activeFilter === "Rejected"
      ? rejected
      : pending;

  return (
    <div className="manage-services">
      <h2>Service Applications Management</h2>

      {/* FILTER BUTTONS */}
      <div className="filter-buttons">
        <button onClick={() => setActiveFilter("Pending")}>
          üïí Pending ({pending.length})
        </button>
        <button onClick={() => setActiveFilter("Approved")}>
          ‚úÖ Approved ({approved.length})
        </button>
        <button onClick={() => setActiveFilter("Rejected")}>
          ‚ùå Rejected ({rejected.length})
        </button>
      </div>

      {/* LIST */}
      <div className="cards-container">
        {filteredApps.map((app) => (
          <div className="service-card" key={app._id}>
            <p><b>Name:</b> {app.fullName}</p>
            <p><b>Service:</b> {app.serviceType}</p>

            {/* DOCUMENTS */}
            {app.documents && (
              <div className="documents-list">
                {Object.entries(app.documents).map(([name, url], i) => (
                  <a key={i} href={url} target="_blank" rel="noreferrer">
                    üìé {name}
                  </a>
                ))}
              </div>
            )}

            {/* REJECTION MESSAGE */}
            {activeFilter === "Rejected" && app.adminRemark && (
              <p className="remark">‚ùå {app.adminRemark}</p>
            )}

            {/* ACTIONS */}
            {activeFilter === "Pending" && (
              <div className="actions">
                <button
                  className="approve-btn"
                  onClick={() => openModal(app._id, "Approved")}
                >
                  Approve
                </button>

                <button
                  className="reject-btn"
                  onClick={() => openModal(app._id, "Rejected")}
                >
                  Reject
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* ================= POPUP MODAL ================= */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-box">
            <h3>
              {modalType === "Approved"
                ? "‚úÖ Approve Application"
                : "‚ùå Reject Application"}
            </h3>

            <textarea
              placeholder={
                modalType === "Approved"
                  ? "Example: Your documents are ready. Come to Panchayat today at 4 PM."
                  : "Enter rejection reason..."
              }
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />

            <div className="modal-actions">
              <button className="confirm-btn" onClick={confirmAction}>
                Confirm
              </button>
              <button
                className="cancel-btn"
                onClick={() => setShowModal(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageServices;
