import React, { useEffect, useState } from "react";
import "./ManageGallery.css";

const ManageGallery = ({ token }) => { // Accept token as a prop instead
  const [images, setImages] = useState([]);
  const [file, setFile] = useState(null);
  const [caption, setCaption] = useState("");
  const [selectedFileName, setSelectedFileName] = useState("");

  const fetchImages = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/gallery");
      const data = await res.json();
      setImages(data);
    } catch (error) {
      console.error("Error fetching images:", error);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setSelectedFileName(selectedFile ? selectedFile.name : "");
  };

  const uploadImage = async (e) => {
    e.preventDefault();
    if (!file) return;

    const formData = new FormData();
    formData.append("image", file);
    formData.append("caption", caption);

    try {
      await fetch("http://localhost:5000/api/gallery", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });

      setFile(null);
      setCaption("");
      setSelectedFileName("");
      fetchImages();
    } catch (error) {
      console.error("Error uploading image:", error);
    }
  };

  const deleteImage = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/gallery/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchImages();
    } catch (error) {
      console.error("Error deleting image:", error);
    }
  };

  return (
    <div className="manage-gallery">
      <h2>Manage Gallery</h2>

      <form className="upload-form" onSubmit={uploadImage}>
        <div className="form-group">
          <div className="file-input-wrapper">
            <input
              type="file"
              id="file-input"
              required
              onChange={handleFileChange}
              accept="image/*"
            />
            <label htmlFor="file-input" className="file-input-label">
              {selectedFileName || "Choose an image to upload"}
            </label>
          </div>

          <input
            className="caption-input"
            placeholder="Enter image caption..."
            value={caption}
            onChange={(e) => setCaption(e.target.value)}
          />

          <button className="upload-button" type="submit">
            Upload Image
          </button>
        </div>
      </form>

      {images.length === 0 ? (
        <div className="empty-state">
          <div className="empty-state-icon">üñºÔ∏è</div>
          <p className="empty-state-text">No images yet. Upload your first image!</p>
        </div>
      ) : (
        <div className="gallery-grid">
          {images.map((img) => (
            <div key={img._id} className="image-card">
              <div className="image-wrapper">
                <img
                  src={`http://localhost:5000${img.image}`}
                  alt={img.caption || "Gallery image"}
                />
                <div className="image-overlay"></div>
              </div>
              <div className="card-actions">
                <button
                  className="delete-button"
                  onClick={() => deleteImage(img._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManageGallery;
