import React, { useEffect, useState } from "react";
import "./Gallery.css";

const Gallery = () => {
  const [gallery, setGallery] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [lightbox, setLightbox] = useState(null);

  useEffect(() => {
    const fetchGallery = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/gallery");
        if (!res.ok) throw new Error("Failed to fetch gallery");
        
        const data = await res.json();
        setGallery(data);
      } catch (err) {
        setError("Unable to load gallery images");
        console.error("Error fetching gallery:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchGallery();
  }, []);

  // Get unique categories
  const categories = ["all", ...new Set(gallery.map(item => item.category).filter(Boolean))];

  // Filter gallery by category
  const filteredGallery = selectedCategory === "all" 
    ? gallery 
    : gallery.filter(item => item.category === selectedCategory);

  // Lightbox handlers
  const openLightbox = (item) => {
    setLightbox(item);
    document.body.style.overflow = 'hidden';
  };

  const closeLightbox = () => {
    setLightbox(null);
    document.body.style.overflow = 'auto';
  };

  const navigateLightbox = (direction) => {
    const currentIndex = filteredGallery.findIndex(item => item._id === lightbox._id);
    const newIndex = direction === 'next' 
      ? (currentIndex + 1) % filteredGallery.length 
      : (currentIndex - 1 + filteredGallery.length) % filteredGallery.length;
    setLightbox(filteredGallery[newIndex]);
  };

  if (loading) {
    return (
      <div className="gallery-container">
        <div className="gallery-loading">
          <div className="spinner"></div>
          <p>Loading gallery...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="gallery-container">
        <div className="gallery-error">
          <svg className="error-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="gallery-container">
      <div className="gallery-content">
        {/* Header Section */}
        <div className="gallery-header">
          <div className="header-icon">
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
          </div>
          <h1 className="gallery-title">Village Gallery</h1>
          <p className="gallery-subtitle">Capturing moments and memories from our beautiful village</p>

          {/* Image Count */}
          <div className="gallery-count">
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
            <span>{filteredGallery.length} {filteredGallery.length === 1 ? 'Photo' : 'Photos'}</span>
          </div>
        </div>

        {/* Category Filter */}
        {categories.length > 1 && (
          <div className="category-filter">
            {categories.map((category) => (
              <button
                key={category}
                className={selectedCategory === category ? "category-btn active" : "category-btn"}
                onClick={() => setSelectedCategory(category)}
              >
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </button>
            ))}
          </div>
        )}

        {/* Gallery Grid */}
        {filteredGallery.length === 0 ? (
          <div className="gallery-empty">
            <div className="empty-icon">
              <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
            </div>
            <p>No images available in this category</p>
          </div>
        ) : (
          <div className="gallery-grid">
            {filteredGallery.map((item, index) => (
              <div 
                className="gallery-card" 
                key={item._id}
                onClick={() => openLightbox(item)}
                style={{ animationDelay: `${index * 0.05}s` }}
              >
                <div className="image-wrapper">
                  <img 
                    src={`http://localhost:5000${item.image}`} 
                    alt={item.caption || "Gallery image"}
                    loading="lazy"
                  />
                  <div className="image-overlay">
                    <svg className="zoom-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM10 7v3m0 0v3m0-3h3m-3 0H7" />
                    </svg>
                  </div>
                </div>
                <div className="card-info">
                  {item.caption && <h4 className="image-caption">{item.caption}</h4>}
                  {item.category && (
                    <span className="image-category">
                      <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M7 7h.01M7 3h5c.512 0 1.024.195 1.414.586l7 7a2 2 0 010 2.828l-7 7a2 2 0 01-2.828 0l-7-7A1.994 1.994 0 013 12V7a4 4 0 014-4z" />
                      </svg>
                      {item.category}
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Lightbox Modal */}
      {lightbox && (
        <div className="lightbox-overlay" onClick={closeLightbox}>
          <button className="lightbox-close" onClick={closeLightbox}>
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>

          <button 
            className="lightbox-nav lightbox-prev" 
            onClick={(e) => { e.stopPropagation(); navigateLightbox('prev'); }}
          >
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7" />
            </svg>
          </button>

          <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
            <img 
              src={`http://localhost:5000${lightbox.image}`} 
              alt={lightbox.caption || "Gallery image"}
            />
            {(lightbox.caption || lightbox.category) && (
              <div className="lightbox-info">
                {lightbox.caption && <h3>{lightbox.caption}</h3>}
                {lightbox.category && <p>{lightbox.category}</p>}
              </div>
            )}
          </div>

          <button 
            className="lightbox-nav lightbox-next" 
            onClick={(e) => { e.stopPropagation(); navigateLightbox('next'); }}
          >
            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      )}
    </div>
  );
};

export default Gallery;
