import React, { useEffect, useState } from "react";

const ManageAbout = () => {
  const [form, setForm] = useState({
    name: "",
    history: "",
    populationTotal: "",
    populationMale: "",
    populationFemale: "",
    area: "",
    description: "",
  });

  const token = localStorage.getItem("token");

  useEffect(() => {
    fetch("http://localhost:5000/api/village")
      .then(res => res.json())
      .then(data => data && setForm(data));
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const saveData = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/api/village", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(form),
    });

    alert("Village details updated");
  };

  return (
    <form onSubmit={saveData} style={{ padding: "20px" }}>
      <h2>Manage Village About</h2>

      {Object.keys(form).map((key) => (
        <input
          key={key}
          name={key}
          placeholder={key}
          value={form[key] || ""}
          onChange={handleChange}
          style={{ display: "block", marginBottom: "10px", width: "100%" }}
        />
      ))}

      <button type="submit">Save</button>
    </form>
  );
};

export default ManageAbout;
