import React, { useEffect, useState } from "react";
import "./Notices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const Notices = () => {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [selectedNotice, setSelectedNotice] = useState(null); // ‚úÖ NEW

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await fetch(`${API}/api/notices`);
        if (!res.ok) throw new Error("Failed to fetch notices");

        const data = await res.json();

        const sorted = Array.isArray(data)
          ? data.sort((a, b) => {
              if (a.isImportant === b.isImportant) {
                return new Date(b.createdAt) - new Date(a.createdAt);
              }
              return b.isImportant - a.isImportant;
            })
          : [];

        setNotices(sorted);
      } catch (err) {
        setError("Unable to load notices");
      } finally {
        setLoading(false);
      }
    };

    fetchNotices();
  }, []);

  if (loading) {
    return (
      <div className="notice-container">
        <div className="loading-wrapper">
          <div className="spinner"></div>
          <p className="loading-text">‡§∏‡•Ç‡§ö‡§®‡§æ ‡§≤‡•ã‡§° ‡§π‡•ã‡§§ ‡§Ü‡§π‡•á‡§§....</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="notice-container">
        <div className="error-box">
          <p className="error-text">‚ö†Ô∏è {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="notice-container">
      <div className="notice-content">
        <div className="notice-header">
          <h1 className="header-title">‡§®‡§µ‡•Ä‡§® ‡§∏‡•Ç‡§ö‡§®‡§æ</h1>
          <p className="header-subtitle">
            ‡§®‡§µ‡•Ä‡§® ‡§ò‡•ã‡§∑‡§£‡§æ‡§Ç‡§ö‡•Ä ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä ‡§Ø‡•á‡§•‡•á ‡§Æ‡§ø‡§≥‡§µ‡§æ
          </p>
        </div>

        <div className="notice-grid">
          {notices.map((notice) => (
            <div
              key={notice._id}
              className={`notice-card ${
                notice.isImportant ? "notice-important" : ""
              }`}
            >
              {notice.isImportant && (
                <div className="important-ribbon">‡§Æ‡§π‡§§‡•ç‡§µ‡§æ‡§ö‡•á</div>
              )}

              <div className="notice-body">
                <h3 className="notice-title">{notice.title}</h3>
                <p className="notice-date">
                  {new Date(notice.createdAt).toLocaleDateString()}
                </p>

                {/* Short description */}
                <p className="notice-description">
                  {notice.description.slice(0, 120)}...
                </p>

                <button
                  className="view-btn"
                  onClick={() => setSelectedNotice(notice)}
                >
                  View Full Notice ‚Üí
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ================= MODAL ================= */}
      {selectedNotice && (
        <div className="modal-overlay" onClick={() => setSelectedNotice(null)}>
          <div
            className="modal-box"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              className="modal-close"
              onClick={() => setSelectedNotice(null)}
            >
              ‚úï
            </button>

            <h2>{selectedNotice.title}</h2>
            <p className="modal-date">
              {new Date(selectedNotice.createdAt).toLocaleDateString()}
            </p>

            <p className="modal-description">
              {selectedNotice.description}
            </p>

            {selectedNotice.attachment && (
              selectedNotice.attachment.match(/\.(jpg|png|jpeg|webp)$/i) ? (
                <img
                  src={selectedNotice.attachment}
                  alt="Notice"
                  className="modal-image"
                />
              ) : (
                <a
                  href={selectedNotice.attachment}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="notice-attachment"
                >
                  üìÑ View / Download Document
                </a>
              )
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Notices;
