import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [notices, setNotices] = useState([]);

  const token = localStorage.getItem("token");

  const fetchNotices = async () => {
    const res = await fetch("http://localhost:5000/api/notices");
    const data = await res.json();
    setNotices(data);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  const addNotice = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/api/notices", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ title, description }),
    });

    setTitle("");
    setDescription("");
    fetchNotices();
  };

  const deleteNotice = async (id) => {
    await fetch(`http://localhost:5000/api/notices/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    fetchNotices();
  };

  return (
    <div className="manage-notices">
      <h2>Manage Notices</h2>

      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Add Notice</button>
      </form>

      <div className="notice-list">
        {notices.map((n) => (
          <div className="notice-card" key={n._id}>
            <h4>{n.title}</h4>
            <p>{n.description}</p>
            <button onClick={() => deleteNotice(n._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageNotices;
