import React, { useState } from "react";
import "./HomeSlider.css"; // Import the CSS file

const slides = [
  {
    id: 1,
    title: "Welcome to Gram Panchayat",
    description: "Empowering local communities with technology",
    image: "https://source.unsplash.com/800x400/?village,technology",
  },
  {
    id: 2,
    title: "Community Resources",
    description: "Find and share resources in your area easily",
    image: "https://source.unsplash.com/800x400/?community,village",
  },
  {
    id: 3,
    title: "Stay Connected",
    description: "Get real-time updates on local projects and events",
    image: "https://source.unsplash.com/800x400/?meeting,village",
  },
];

const HomeSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevSlide = () => {
    setCurrentIndex(currentIndex === 0 ? slides.length - 1 : currentIndex - 1);
  };

  const nextSlide = () => {
    setCurrentIndex(currentIndex === slides.length - 1 ? 0 : currentIndex + 1);
  };

  return (
    <div className="slider">
      {slides.map((slide, index) => (
        <div
          key={slide.id}
          className={`slide ${index === currentIndex ? "active" : ""}`}
        >
          <img src={slide.image} alt={slide.title} />
          <div className="caption">
            <h2>{slide.title}</h2>
            <p>{slide.description}</p>
          </div>
        </div>
      ))}

      <button className="prev" onClick={prevSlide}>
        &#10094;
      </button>
      <button className="next" onClick={nextSlide}>
        &#10095;
      </button>

      <div className="dots">
        {slides.map((_, index) => (
          <span
            key={index}
            className={index === currentIndex ? "dot active" : "dot"}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default HomeSlider;
