import React, { useEffect, useState } from "react";

const API = "https://backend-9i6n.onrender.com";

const About = () => {
  const [village, setVillage] = useState(null);

  useEffect(() => {
    fetch(`${API}/api/village`)
      .then((res) => res.json())
      .then((data) => setVillage(data))
      .catch((err) => {
        console.error("Failed to fetch village data", err);
      });
  }, []);

  if (!village) {
    return (
      <div style={styles.loadingContainer}>
        <p style={styles.loadingText}>Loading village information...</p>
      </div>
    );
  }

  return (
    <div style={styles.pageContainer}>
      {/* HEADER */}
      <header style={styles.header}>
        <div style={styles.headerContent}>
          <h1 style={styles.headerTitle}>Welcome to {village.name}</h1>
          <p style={styles.slogan}>Clean Village â€¢ Strong Community â€¢ Bright Future</p>
        </div>
      </header>

      {/* MAIN CONTENT */}
      <main style={styles.mainContent}>
        {/* HISTORY SECTION */}
        <section style={styles.section}>
          <h2 style={styles.sectionTitle}>Village History</h2>
          <div style={styles.contentBox}>
            <p style={styles.text}>{village.history}</p>
          </div>
        </section>

        {/* STATISTICS SECTION */}
        <section style={styles.section}>
          <h2 style={styles.sectionTitle}>Village Statistics</h2>
          <div style={styles.statsGrid}>
            <StatCard
              icon="ðŸ‘¥"
              value={village.populationTotal}
              label="Total Population"
            />
            <StatCard
              icon="ðŸ‘¨â€ðŸŒ¾"
              value={village.populationMale}
              label="Male Population"
            />
            <StatCard
              icon="ðŸ‘©â€ðŸŒ¾"
              value={village.populationFemale}
              label="Female Population"
            />
            <StatCard
              icon="ðŸ—ºï¸"
              value={village.area}
              label="Area (sq km)"
            />
          </div>
        </section>

        {/* DESCRIPTION SECTION */}
        {village.description && (
          <section style={styles.section}>
            <h2 style={styles.sectionTitle}>About Our Village</h2>
            <div style={styles.contentBox}>
              <p style={styles.text}>{village.description}</p>
            </div>
          </section>
        )}

        {/* VISION SECTION */}
        <section style={{...styles.section, ...styles.visionSection}}>
          <h2 style={styles.sectionTitle}>Our Vision</h2>
          <ul style={styles.visionList}>
            <VisionItem text="Transparent and digital governance for rural development" />
            <VisionItem text="Clean and green village environment with sustainable farming" />
            <VisionItem text="Quality education and healthcare facilities for all villagers" />
            <VisionItem text="Strong agricultural growth and employment opportunities" />
          </ul>
        </section>
      </main>
    </div>
  );
};

// Stat Card Component
const StatCard = ({ icon, value, label }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      style={{
        ...styles.statCard,
        ...(isHovered ? styles.statCardHover : {}),
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <span style={styles.statIcon}>{icon}</span>
      <h3 style={styles.statValue}>{value}</h3>
      <p style={styles.statLabel}>{label}</p>
    </div>
  );
};

// Vision Item Component
const VisionItem = ({ text }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <li
      style={{
        ...styles.visionItem,
        ...(isHovered ? styles.visionItemHover : {}),
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {text}
    </li>
  );
};

// Styles Object
const styles = {
  pageContainer: {
    width: "100%",
    minHeight: "100vh",
    background: "linear-gradient(to bottom, #f5f8f2 0%, #eaf3e3 100%)",
    margin: 0,
    padding: 0,
  },
  loadingContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    background: "linear-gradient(to bottom, #f5f8f2 0%, #eaf3e3 100%)",
  },
  loadingText: {
    fontSize: "1.2rem",
    color: "#6b8e4e",
    fontWeight: 500,
  },
  header: {
    background: "linear-gradient(135deg, #a4c89b 0%, #89b37e 100%)",
    padding: "50px 20px",
    textAlign: "center",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
  },
  headerContent: {
    maxWidth: "1200px",
    margin: "0 auto",
  },
  headerTitle: {
    fontSize: "2.5rem",
    margin: "0 0 12px 0",
    fontWeight: 700,
    color: "#ffffff",
    textShadow: "2px 2px 4px rgba(0, 0, 0, 0.1)",
  },
  slogan: {
    fontSize: "1rem",
    margin: 0,
    color: "#ffffff",
    opacity: 0.95,
    fontWeight: 400,
  },
  mainContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    padding: "20px",
  },
  section: {
    background: "#ffffff",
    borderRadius: "12px",
    padding: "35px 25px",
    marginBottom: "25px",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.06)",
  },
  sectionTitle: {
    fontSize: "1.6rem",
    color: "#5a7a47",
    marginBottom: "20px",
    borderLeft: "4px solid #8ba97d",
    paddingLeft: "15px",
    fontWeight: 600,
  },
  contentBox: {
    background: "linear-gradient(to right, #f8faf6 0%, #f2f7ee 100%)",
    padding: "20px",
    borderRadius: "8px",
    borderLeft: "3px solid #a8c89f",
  },
  text: {
    fontSize: "1rem",
    lineHeight: 1.7,
    color: "#4a5a42",
    margin: 0,
    textAlign: "justify",
  },
  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "20px",
    marginTop: "10px",
  },
  statCard: {
    background: "linear-gradient(135deg, #f8faf6 0%, #f0f6ed 100%)",
    border: "2px solid #d4e3cd",
    padding: "25px 15px",
    borderRadius: "10px",
    textAlign: "center",
    transition: "all 0.3s ease",
    cursor: "pointer",
  },
  statCardHover: {
    borderColor: "#8ba97d",
    boxShadow: "0 6px 20px rgba(139, 169, 125, 0.15)",
    transform: "translateY(-4px)",
  },
  statIcon: {
    fontSize: "2.2rem",
    display: "block",
    marginBottom: "12px",
  },
  statValue: {
    fontSize: "1.8rem",
    margin: "8px 0",
    fontWeight: 700,
    color: "#5a7a47",
  },
  statLabel: {
    fontSize: "0.9rem",
    color: "#6b7d63",
    margin: 0,
    fontWeight: 500,
  },
  visionSection: {
    background: "linear-gradient(to bottom, #f8faf6 0%, #f0f6ed 100%)",
    border: "2px solid #d4e3cd",
  },
  visionList: {
    listStyle: "none",
    padding: 0,
    margin: 0,
  },
  visionItem: {
    fontSize: "1rem",
    padding: "14px 20px",
    margin: "10px 0",
    background: "#ffffff",
    borderRadius: "8px",
    borderLeft: "3px solid #8ba97d",
    color: "#4a5a42",
    transition: "all 0.3s ease",
    cursor: "pointer",
  },
  visionItemHover: {
    background: "linear-gradient(to right, #f5f9f2 0%, #ffffff 100%)",
    paddingLeft: "26px",
    boxShadow: "0 4px 12px rgba(139, 169, 125, 0.1)",
    borderLeftWidth: "5px",
  },
};

// Media Query Styles (for responsive design)
if (typeof window !== 'undefined') {
  const mediaQuery = window.matchMedia('(max-width: 768px)');
  
  if (mediaQuery.matches) {
    styles.headerTitle.fontSize = "1.8rem";
    styles.slogan.fontSize = "0.9rem";
    styles.header.padding = "40px 15px";
    styles.section.padding = "25px 20px";
    styles.sectionTitle.fontSize = "1.4rem";
    styles.statsGrid.gridTemplateColumns = "repeat(auto-fit, minmax(150px, 1fr))";
    styles.statsGrid.gap = "15px";
  }
  
  const smallMediaQuery = window.matchMedia('(max-width: 480px)');
  
  if (smallMediaQuery.matches) {
    styles.headerTitle.fontSize = "1.5rem";
    styles.slogan.fontSize = "0.85rem";
    styles.header.padding = "30px 15px";
    styles.section.padding = "20px 15px";
    styles.sectionTitle.fontSize = "1.2rem";
    styles.statsGrid.gridTemplateColumns = "1fr 1fr";
    styles.statsGrid.gap = "12px";
    styles.statCard.padding = "20px 12px";
    styles.statIcon.fontSize = "1.8rem";
    styles.statValue.fontSize = "1.5rem";
    styles.text.fontSize = "0.95rem";
  }
}

export default About;
