import React, { useEffect, useState } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const token = localStorage.getItem("token");

  const [stats, setStats] = useState({
    totalProjects: 0,
    totalApplications: 0,
    approvedDocs: 0,
    rejectedDocs: 0,
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const res = await fetch(
        "http://localhost:5000/api/admin/dashboard/stats",
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const data = await res.json();
      setStats(data);
    } catch (err) {
      console.error("Dashboard fetch failed", err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <p style={{ padding: "20px" }}>Loading dashboard...</p>;
  }

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-title">Admin Dashboard</h2>
      <p className="dashboard-subtitle">
        Welcome to Digital Gram Panchayat Admin Panel
      </p>

      <div className="dashboard-boxes">
        <div className="dashboard-card">
          <h3>Total Projects</h3>
          <p>{stats.totalProjects}</p>
        </div>

        <div className="dashboard-card">
          <h3>Total Applications</h3>
          <p>{stats.totalApplications}</p>
        </div>

        <div className="dashboard-card approved">
          <h3>Approved Documents</h3>
          <p>{stats.approvedDocs}</p>
        </div>

        <div className="dashboard-card rejected">
          <h3>Rejected Documents</h3>
          <p>{stats.rejectedDocs}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
