import React, { useEffect, useState } from "react";
import "./ManageSchemes.css";

/* âœ… CLOUD BACKEND */
const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const ManageSchemes = () => {
  const [schemes, setSchemes] = useState([]);
  const [editingId, setEditingId] = useState(null);

  const [form, setForm] = useState({
    title: "",
    description: "",
    benefits: "",
    eligibility: "",
    applyProcess: "",
    applyLink: "",
    schemeType: "Central",
  });

  const token = localStorage.getItem("token");

  /* ================= FETCH ================= */
  const fetchSchemes = async () => {
    try {
      const res = await fetch(`${API}/api/schemes`);
      const data = await res.json();
      setSchemes(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Fetch schemes error:", err);
    }
  };

  useEffect(() => {
    fetchSchemes();
  }, []);

  /* ================= INPUT ================= */
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  /* ================= ADD / UPDATE ================= */
  const submitScheme = async (e) => {
    e.preventDefault();

    const url = editingId
      ? `${API}/api/schemes/${editingId}`
      : `${API}/api/schemes`;

    const method = editingId ? "PUT" : "POST";

    try {
      await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });

      resetForm();
      fetchSchemes();
    } catch (err) {
      console.error("Submit scheme error:", err);
    }
  };

  /* ================= EDIT ================= */
  const startEdit = (scheme) => {
    setEditingId(scheme._id);
    setForm({
      title: scheme.title,
      description: scheme.description || "",
      benefits: scheme.benefits || "",
      eligibility: scheme.eligibility || "",
      applyProcess: scheme.applyProcess || "",
      applyLink: scheme.applyLink || "",
      schemeType: scheme.schemeType || "Central",
    });
  };

  /* ================= DELETE ================= */
  const deleteScheme = async (id) => {
    if (!window.confirm("Delete this scheme?")) return;

    try {
      await fetch(`${API}/api/schemes/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      fetchSchemes();
    } catch (err) {
      console.error("Delete scheme error:", err);
    }
  };

  /* ================= RESET ================= */
  const resetForm = () => {
    setEditingId(null);
    setForm({
      title: "",
      description: "",
      benefits: "",
      eligibility: "",
      applyProcess: "",
      applyLink: "",
      schemeType: "Central",
    });
  };

  return (
    <div className="manage-schemes">
      <h2>Manage Government Schemes</h2>

      {/* FORM */}
      <form className="scheme-form" onSubmit={submitScheme}>
        <h3 className="schemedata">Enter Scheme Data</h3>

        <input
          name="title"
          placeholder="Title"
          value={form.title}
          onChange={handleChange}
          required
        />

        <textarea
          name="description"
          placeholder="Description"
          value={form.description}
          onChange={handleChange}
        />

        <textarea
          name="benefits"
          placeholder="Benefits"
          value={form.benefits}
          onChange={handleChange}
        />

        <textarea
          name="eligibility"
          placeholder="Eligibility"
          value={form.eligibility}
          onChange={handleChange}
        />

        <textarea
          name="applyProcess"
          placeholder="Apply Process"
          value={form.applyProcess}
          onChange={handleChange}
        />

        <input
          type="url"
          name="applyLink"
          placeholder="Apply Link (https://...)"
          value={form.applyLink}
          onChange={handleChange}
        />

        <select
          name="schemeType"
          value={form.schemeType}
          onChange={handleChange}
        >
          <option value="Central">Central</option>
          <option value="State">State</option>
        </select>

        <div className="form-actions">
          <button type="submit">
            {editingId ? "Update Scheme" : "Add Scheme"}
          </button>

          {editingId && (
            <button
              type="button"
              className="cancel-btn"
              onClick={resetForm}
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      {/* LIST */}
      <div className="scheme-list">
        {schemes.map((s) => (
          <div className="scheme-card" key={s._id}>
            <h4>{s.title}</h4>
            <p>
              <strong>Type:</strong> {s.schemeType}
            </p>

            {s.applyLink && (
              <a
                href={s.applyLink}
                target="_blank"
                rel="noopener noreferrer"
                className="apply-link"
              >
                ðŸ”— Apply Here
              </a>
            )}

            <div className="card-actions">
              <button onClick={() => startEdit(s)}>Update</button>
              <button onClick={() => deleteScheme(s._id)}>
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageSchemes;
