  ],

  "‡§Æ‡•É‡§§‡•ç‡§Ø‡•Ç ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞": [
    { name: "deathSlip", label: "üè• ‡§Æ‡•É‡§§‡•ç‡§Ø‡•Ç ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞ / ‡§∞‡•Å‡§ó‡•ç‡§£‡§æ‡§≤‡§Ø‡§æ‡§ö‡•Ä ‡§™‡§æ‡§µ‡§§‡•Ä" },
    { name: "deceasedAadhaar", label: "üÜî ‡§Æ‡•É‡§§ ‡§µ‡•ç‡§Ø‡§ï‡•ç‡§§‡•Ä‡§ö‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
    { name: "applicantAadhaar", label: "üÜî ‡§Ö‡§∞‡•ç‡§ú‡§¶‡§æ‡§∞‡§æ‡§ö‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
  ],

  "‡§â‡§§‡•ç‡§™‡§®‡•ç‡§® ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞": [
    { name: "aadhaar", label: "üÜî ‡§Ö‡§∞‡•ç‡§ú‡§¶‡§æ‡§∞‡§æ‡§ö‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
    { name: "rationCard", label: "üçö ‡§∞‡•á‡§∂‡§® ‡§ï‡§æ‡§∞‡•ç‡§°" },
    { name: "incomeProof", label: "üíº ‡§â‡§§‡•ç‡§™‡§®‡•ç‡§®‡§æ‡§ö‡§æ ‡§™‡•Å‡§∞‡§æ‡§µ‡§æ" },
  ],

  "‡§∞‡§π‡§ø‡§µ‡§æ‡§∏‡•Ä ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞": [
    { name: "aadhaar", label: "üÜî ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
    { name: "electricityBill", label: "‚ö° ‡§µ‡•Ä‡§ú ‡§¨‡§ø‡§≤" },
    { name: "rationCard", label: "üçö ‡§∞‡•á‡§∂‡§® ‡§ï‡§æ‡§∞‡•ç‡§°" },
  ],

  "‡§µ‡§ø‡§µ‡§æ‡§π ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞": [
    { name: "brideGroomAadhaar", label: "üÜî ‡§µ‡§∞‡§æ‡§ö‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
    { name: "brideAadhaar", label: "üÜî ‡§µ‡§ß‡•Ç‡§ö‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
    { name: "ageProof", label: "üìÖ ‡§µ‡§Ø‡§æ‡§ö‡§æ ‡§™‡•Å‡§∞‡§æ‡§µ‡§æ" },
    { name: "marriagePhoto", label: "üíç ‡§µ‡§ø‡§µ‡§æ‡§π ‡§∏‡§Æ‡§æ‡§∞‡§Ç‡§≠‡§æ‡§ö‡•á ‡§õ‡§æ‡§Ø‡§æ‡§ö‡§ø‡§§‡•ç‡§∞" },
    { name: "witnessAadhaar", label: "üë• ‡§∏‡§æ‡§ï‡•ç‡§∑‡•Ä‡§¶‡§æ‡§∞‡§æ‡§Ç‡§ö‡•á ‡§Ü‡§ß‡§æ‡§∞ ‡§ï‡§æ‡§∞‡•ç‡§°" },
  ],
};

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",
    deceasedName: "",
    dateOfDeath: "",
  });

  const [files, setFiles] = useState({});
  const [fileErrors, setFileErrors] = useState({});
  const [loading, setLoading] = useState(false);

  /* ================= PREFILL SERVICE ================= */
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const service = params.get("service");
    if (service) {
      setFormData((p) => ({ ...p, serviceType: service }));
    }
  }, [location.search]);

  /* ================= INPUT ================= */
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /* ================= FILE VALIDATION ================= */
  const handleFileChange = (e) => {
    const { name, files: selected } = e.target;
    const file = selected[0];
    if (!file) return;

    const allowedTypes = [
      "image/jpeg",
      "image/png",
      "image/jpg",
      "application/pdf",
    ];

    if (!allowedTypes.includes(file.type)) {
      setFileErrors((p) => ({
        ...p,
        [name]: "‡§´‡§ï‡•ç‡§§ JPG, PNG ‡§ï‡§ø‡§Ç‡§µ‡§æ PDF ‡§´‡§æ‡§à‡§≤ ‡§Ö‡§™‡§≤‡•ã‡§° ‡§ï‡§∞‡§æ",
      }));
      e.target.value = "";
      return;
    }

    if (file.size > MAX_FILE_SIZE) {
      setFileErrors((p) => ({
        ...p,
        [name]: "‡§´‡§æ‡§à‡§≤‡§ö‡§æ ‡§Ü‡§ï‡§æ‡§∞ 500 KB ‡§™‡•á‡§ï‡•ç‡§∑‡§æ ‡§ú‡§æ‡§∏‡•ç‡§§ ‡§®‡§∏‡§æ‡§µ‡§æ",
      }));
      e.target.value = "";
      return;
    }

    setFileErrors((p) => ({ ...p, [name]: "" }));
    setFiles((p) => ({ ...p, [name]: file }));
  };

  /* ================= SUBMIT ================= */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const data = new FormData();
    Object.entries(formData).forEach(([k, v]) => data.append(k, v));
    Object.entries(files).forEach(([k, f]) => data.append(k, f));

    try {
      await axios.post(`${API}/api/services/apply`, data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("‡§Ö‡§∞‡•ç‡§ú ‡§Ø‡§∂‡§∏‡•ç‡§µ‡•Ä‡§∞‡•Ä‡§§‡•ç‡§Ø‡§æ ‡§∏‡§æ‡§¶‡§∞ ‡§ù‡§æ‡§≤‡§æ ‚úÖ");
      setFormData({
        serviceType: "",
        fullName: "",
        address: "",
        mobile: "",
        deceasedName: "",
        dateOfDeath: "",
      });
      setFiles({});
      setFileErrors({});
    } catch {
      alert("‡§Ö‡§∞‡•ç‡§ú ‡§∏‡§æ‡§¶‡§∞ ‡§π‡•ã‡§ä ‡§∂‡§ï‡§≤‡§æ ‡§®‡§æ‡§π‡•Ä ‚ùå");
    } finally {
      setLoading(false);
    }
  };

