import React, { useEffect, useState } from "react";
import "./HeroSection.css";

const API =
  process.env.REACT_APP_API_URL || "https://backend-9i6n.onrender.com";

const HeroSection = () => {
  const [members, setMembers] = useState([]);

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const res = await fetch(`${API}/api/members`);
        const data = await res.json();

        // Only Sarpanch & Up-Sarpanch
        const topMembers = data.filter(
          (m) =>
            m.role?.toLowerCase() === "sarpanch" ||
            m.role?.toLowerCase() === "up-sarpanch"
        );

        setMembers(topMembers);
      } catch (err) {
        console.error("Member fetch error:", err);
      }
    };

    fetchMembers();
  }, []);

  return (
    <div className="hero-container">
      <img
        src="https://images.unsplash.com/photo-1586773860418-d37222d8fce3"
        alt="Village"
        className="hero-image"
      />

      <div className="hero-overlay">
        <h1>Digital Gram Panchayat</h1>
        <h2>Palshi</h2>
        <p>
          Welcome to the official portal of Palshi Gram Panchayat.
          Serving citizens with transparency and technology.
        </p>
      </div>

      {/* ðŸ‘¥ MEMBER CARDS */}
      <div className="hero-members">
        {members.map((member) => (
          <div className="member-card" key={member._id}>
            <img
              src={member.photo}
              alt={member.name}
              className="member-photo"
            />
            <h4>{member.name}</h4>
            <span>{member.role}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HeroSection;
