import React, { useEffect, useState } from "react";
import "./Notices.css";

const API = process.env.REACT_APP_API_URL; // ✅ cloud backend

const Notices = () => {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchNotices = async () => {
      try {
        const res = await fetch(`${API}/api/notices`);

        if (!res.ok) throw new Error("Failed to fetch notices");

        const data = await res.json();
        setNotices(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error(err);
        setError("Unable to load notices");
      } finally {
        setLoading(false);
      }
    };

    fetchNotices();
  }, []);

  if (loading) return <p>Loading notices...</p>;
  if (error) return <p>⚠️ {error}</p>;

  return (
    <div className="notice-container">
      <h2>Notice Board</h2>

      {notices.map((n) => {
        const fileUrl =
          n.attachment?.startsWith("http")
            ? n.attachment
            : n.attachment
            ? `${API}${n.attachment}`
            : null;

        return (
          <div key={n._id} className="notice-card">
            <h3>{n.title}</h3>
            <p>{n.description}</p>

            {fileUrl && (
              <a href={fileUrl} target="_blank" rel="noreferrer">
                View Attachment
              </a>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Notices;
