import React, { useEffect, useState } from "react";
import "./ManageServices.css";

const ManageServices = () => {
  const [applications, setApplications] = useState([]);
  const token = localStorage.getItem("token");

  const fetchApplications = async () => {
    const res = await fetch("http://localhost:5000/api/admin/services", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    const data = await res.json();
    setApplications(data);
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  const updateStatus = async (id, status) => {
    await fetch(`http://localhost:5000/api/admin/services/${id}/status`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ status }),
    });

    fetchApplications();
  };

  const pending = applications.filter(a => a.status === "Pending");
  const approved = applications.filter(a => a.status === "Approved");
  const rejected = applications.filter(a => a.status === "Rejected");

  return (
    <div className="manage-services">

      <h2>Service Applications Management</h2>

      {/* üî¥ PENDING SECTION */}
      <section className="section pending">
        <h3>üïí Pending Applications</h3>
        {pending.length === 0 && <p>No pending applications</p>}

        {pending.map(app => (
          <div className="service-card" key={app._id}>
            <p><b>Name:</b> {app.fullName}</p>
            <p><b>Service:</b> {app.serviceType}</p>

            <div className="actions">
              <button
                className="approve-btn"
                onClick={() => updateStatus(app._id, "Approved")}
              >
                Approve
              </button>

              <button
                className="reject-btn"
                onClick={() => updateStatus(app._id, "Rejected")}
              >
                Reject
              </button>
            </div>
          </div>
        ))}
      </section>

      {/* üü¢ APPROVED SECTION */}
      <section className="section approved">
        <h3>‚úÖ Approved Applications</h3>
        {approved.length === 0 && <p>No approved applications</p>}

        {approved.map(app => (
          <div className="service-card approved-card" key={app._id}>
            <p><b>Name:</b> {app.fullName}</p>
            <p><b>Service:</b> {app.serviceType}</p>
          </div>
        ))}
      </section>

      {/* üîµ REJECTED SECTION */}
      <section className="section rejected">
        <h3>‚ùå Rejected Applications</h3>
        {rejected.length === 0 && <p>No rejected applications</p>}

        {rejected.map(app => (
          <div className="service-card rejected-card" key={app._id}>
            <p><b>Name:</b> {app.fullName}</p>
            <p><b>Service:</b> {app.serviceType}</p>
          </div>
        ))}
      </section>

    </div>
  );
};

export default ManageServices;
