import React, { useState } from "react";
import { Link, Outlet, useLocation, useNavigate } from "react-router-dom";
import "./admin.css";

const AdminLayout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const menuItems = [
    { path: "/admin/dashboard", name: "Dashboard", icon: "üìä" },
    { path: "/admin/notices", name: "Notices", icon: "üì¢" },
    { path: "/admin/services", name: "Services", icon: "üõ†Ô∏è" },
    { path: "/admin/schemes", name: "Schemes", icon: "üìã" },
    { path: "/admin/development", name: "Development", icon: "üèóÔ∏è" },
    { path: "/admin/gallery", name: "Gallery", icon: "üñºÔ∏è" },
    { path: "/admin/complaints", name: "Complaints", icon: "üìù" },
    { path: "/admin/contact", name: "Contact", icon: "üìû" },
    { path: "/admin/about", name: "About", icon: "‚ÑπÔ∏è" },
  ];

  const isActive = (path) => location.pathname === path;

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to logout?")) {
      navigate("/login");
    }
  };

  return (
    <div className="admin-container">
      {/* LEFT SIDEBAR */}
      <aside className={`admin-sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <div className="admin-header">
          <div className="admin-logo">
            <div className="logo-icon">GP</div>
            <div className="logo-text">
              <h2>Gram Panchayat</h2>
              <p>Admin Panel</p>
            </div>
          </div>
        </div>

        <div className="admin-profile">
          <div className="profile-avatar">
            <span>A</span>
          </div>
          <div className="profile-info">
            <h3>Administrator</h3>
            <span className="profile-badge">Super Admin</span>
          </div>
        </div>

        <nav className="admin-menu">
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`menu-item ${isActive(item.path) ? 'active' : ''}`}
            >
              <span className="menu-icon">{item.icon}</span>
              <span className="menu-text">{item.name}</span>
              {isActive(item.path) && <span className="active-indicator"></span>}
            </Link>
          ))}
        </nav>

        <div className="admin-footer">
          <button onClick={handleLogout} className="logout-btn">
            <span className="logout-icon">üö™</span>
            <span>Logout</span>
          </button>
        </div>
      </aside>

      {/* RIGHT CONTENT */}
      <main className="admin-content">
        <div className="content-header">
          <button 
            className="sidebar-toggle"
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            ‚ò∞
          </button>
          <div className="breadcrumb">
            <span className="breadcrumb-home">Home</span>
            <span className="breadcrumb-separator">/</span>
            <span className="breadcrumb-current">
              {menuItems.find(item => item.path === location.pathname)?.name || 'Dashboard'}
            </span>
          </div>
          <div className="header-actions">
            <button className="icon-btn" title="Notifications">
              <span className="notification-badge">3</span>
              üîî
            </button>
            <button className="icon-btn" title="Settings">‚öôÔ∏è</button>
          </div>
        </div>

        <div className="content-body">
          <Outlet />
        </div>
      </main>
    </div>
  );
};

export default AdminLayout;
