import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import "./UserProfile.css";

const UserProfile = () => {
  const { token, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const res = await axios.get(
        "http://localhost:5000/api/user/profile",
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      setProfile(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        logout();
        navigate("/login");
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) fetchProfile();
  }, [token]);

  if (loading) return <p className="loading-text">Loading profile...</p>;
  if (!profile) return null;

  const { user, services } = profile;

  return (
    <div className="profile-container">
      <h2>ðŸ‘¤ User Profile</h2>

      {/* USER INFO */}
      <div className="profile-card">
        <p><b>Name:</b> {user.fullName}</p>
        <p><b>Email:</b> {user.email || "N/A"}</p>
        <p><b>Mobile:</b> {user.mobile || "N/A"}</p>
        <p><b>Village:</b> {user.village || "N/A"}</p>
        {user.createdAt && (
          <p>
            <b>Joined:</b>{" "}
            {new Date(user.createdAt).toLocaleDateString()}
          </p>
        )}

        <button
          className="logout-btn"
          onClick={() => {
            logout();
            navigate("/login");
          }}
        >
          Logout
        </button>
      </div>

      {/* USER APPLICATIONS */}
      <h3>ðŸ“„ Your Applications</h3>

      {services.length === 0 ? (
        <p className="no-data">No applications submitted yet</p>
      ) : (
        <div className="applications-list">
          {services.map((s) => (
            <div key={s._id} className="application-card">
              <p><b>Service:</b> {s.serviceType}</p>
              <p>
                <b>Status:</b>{" "}
                <span className={`status ${s.status.toLowerCase()}`}>
                  {s.status}
                </span>
              </p>

              {s.createdAt && (
                <p>
                  <b>Applied On:</b>{" "}
                  {new Date(s.createdAt).toLocaleDateString()}
                </p>
              )}

              {s.status === "Rejected" && (
                <p className="reject-reason">
                  <b>Reason:</b> {s.adminRemark}
                </p>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserProfile;
