import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import "./ManageServices.css";

const ManageServices = () => {
  const { token } = useContext(AuthContext);
  const [services, setServices] = useState([]);
  const [reason, setReason] = useState("");

  const fetchServices = async () => {
    const res = await axios.get(
      "http://localhost:5000/api/admin/services",
      {
        headers: { Authorization: `Bearer ${token}` },
      }
    );
    setServices(res.data);
  };

  useEffect(() => {
    fetchServices();
  }, []);

  const approveService = async (id) => {
    await axios.put(
      `http://localhost:5000/api/admin/services/${id}/approve`,
      {},
      { headers: { Authorization: `Bearer ${token}` } }
    );
    fetchServices();
  };

  const rejectService = async (id) => {
    if (!reason) {
      alert("Please enter rejection reason");
      return;
    }

    await axios.put(
      `http://localhost:5000/api/admin/services/${id}/reject`,
      { reason },
      { headers: { Authorization: `Bearer ${token}` } }
    );

    setReason("");
    fetchServices();
  };

  return (
    <div className="admin-services">
      <h2>Service Applications</h2>

      {services.map((s) => (
        <div key={s._id} className="service-card">
          <p><b>User:</b> {s.appliedBy?.name}</p>
          <p><b>Email:</b> {s.appliedBy?.email}</p>
          <p><b>Village:</b> {s.appliedBy?.village}</p>
          <p><b>Service:</b> {s.serviceType}</p>
          <p><b>Status:</b> {s.status}</p>

          {s.status === "Pending" && (
            <>
              <button onClick={() => approveService(s._id)} className="approve">
                Approve
              </button>

              <textarea
                placeholder="Reason for rejection"
                value={reason}
                onChange={(e) => setReason(e.target.value)}
              />

              <button onClick={() => rejectService(s._id)} className="reject">
                Reject
              </button>
            </>
          )}

          {s.status === "Rejected" && (
            <p className="reject-text">
              ‚ùå Reason: {s.rejectReason}
            </p>
          )}
        </div>
      ))}
    </div>
  );
};

export default ManageServices;
