import React, { useState, useContext, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import "./Navbar.css";
import logo from "../../assets/egram-logo.png";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const [menuOpen, setMenuOpen] = useState(false);

  const firstName = user?.fullName?.split(" ")[0];

  // Initialize Google Translate
  useEffect(() => {
    // Load Google Translate script
    const addScript = document.createElement("script");
    addScript.setAttribute(
      "src",
      "//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit"
    );
    document.body.appendChild(addScript);

    // Initialize function
    window.googleTranslateElementInit = () => {
      new window.google.translate.TranslateElement(
        {
          pageLanguage: "en",
          includedLanguages: "en,mr,hi",
          layout: window.google.translate.TranslateElement.InlineLayout.SIMPLE,
          autoDisplay: false,
        },
        "google_translate_element"
      );
    };

    return () => {
      // Cleanup
      const scripts = document.querySelectorAll('script[src*="translate.google.com"]');
      scripts.forEach(script => script.remove());
    };
  }, []);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-content">

          {/* BRAND */}
          <Link to="/" className="navbar-brand">
            <img src={logo} alt="E Gram Panchayat Logo" className="brand-logo" />
          </Link>

          {/* DESKTOP MENU */}
          <div className="nav-desktop-menu">
            <Link to="/">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/services">Services</Link>
            <Link to="/schemes">Schemes</Link>
            <Link to="/notices">Notices</Link>
            <Link to="/development">Development</Link>
            <Link to="/gallery">Gallery</Link>
            <Link to="/contact">Members</Link>
            
            {/* LANGUAGE TRANSLATE - Desktop */}
            <div className="translate-wrapper desktop-translate">
              <span className="translate-icon">üåê</span>
              <div id="google_translate_element" className="translate-box"></div>
            </div>

            {!user && (
              <Link to="/login" className="login-button">Login</Link>
            )}

            {user && (
              <button
                className="profile-avatar vertical"
                onClick={() => navigate("/profile")}
              >
                <img
                  src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
                  alt="Profile"
                />
                <span className="profile-name">{firstName}</span>
              </button>
            )}
          </div>

          {/* MOBILE BUTTON */}
          <div className="nav-mobile-button">
            <button
              className="nav-toggle-button"
              onClick={() => setMenuOpen(!menuOpen)}
              aria-label="Toggle menu"
            >
              ‚ò∞
            </button>
          </div>
        </div>
      </div>

      {/* MOBILE MENU */}
      {menuOpen && (
        <div className="nav-mobile-menu-wrapper">
          <Link to="/" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/about" onClick={() => setMenuOpen(false)}>About</Link>
          <Link to="/services" onClick={() => setMenuOpen(false)}>Services</Link>
          <Link to="/schemes" onClick={() => setMenuOpen(false)}>Schemes</Link>
          <Link to="/notices" onClick={() => setMenuOpen(false)}>Notices</Link>
          <Link to="/development" onClick={() => setMenuOpen(false)}>Development</Link>
          <Link to="/gallery" onClick={() => setMenuOpen(false)}>Gallery</Link>
          <Link to="/contact" onClick={() => setMenuOpen(false)}>Members</Link>

          {/* LANGUAGE TRANSLATE - Mobile */}
          <div className="mobile-translate-section">
            <div className="mobile-translate-label">
              üåê Language
            </div>
            <div className="mobile-translate-content">
              <div id="google_translate_element_mobile" className="translate-box"></div>
            </div>
          </div>

          {!user && <Link to="/login" onClick={() => setMenuOpen(false)}>Login</Link>}

          {user && (
            <>
              <button
                className="mobile-profile-btn"
                onClick={() => {
                  navigate("/profile");
                  setMenuOpen(false);
                }}
              >
                üë§ My Profile
              </button>

              <button
                onClick={handleLogout}
                className="mobile-logout-button"
              >
                Logout
              </button>
            </>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
