import React, { useEffect, useState } from "react";
import "./HeroSection.css";
import { useTranslation } from "react-i18next";

const API = "https://backend-9i6n.onrender.com";

const HeroSection = () => {
  const { t } = useTranslation();
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    const fetchLeaders = async () => {
      try {
        const res = await fetch(`${API}/api/contacts`);
        const data = await res.json();

        // ✅ SAFE ROLE FILTER (handles case, space, hyphen)
        const filtered = data.filter((m) => {
          const role = m.role?.toLowerCase().replace(/[\s-]/g, "");
          return role === "sarpanch" || role === "upasarpanch";
        });

        setLeaders(filtered.slice(0, 2));
      } catch (err) {
        console.error("Failed to fetch leaders", err);
      }
    };

    fetchLeaders();
  }, []);

  return (
    <div className="hero-wrapper">
      <img
        src="https://images.unsplash.com/photo-1586773860418-d37222d8fce3"
        alt="Village"
        className="hero-bg-image"
      />

      <div className="hero-gradient-overlay"></div>

      <div className="hero-content-grid">
        {/* TEXT SECTION */}
        <div className="hero-text-section">
          <div className="hero-badge">
            {t("hero_badge")}
          </div>

          <h1 className="hero-title">
            {t("hero_title")}
            <br />
            <span className="hero-title-accent">
              {t("hero_title_accent")}
            </span>
          </h1>

          <p className="hero-description">
            {t("hero_description")}
          </p>
        </div>

        {/* LEADERS SECTION */}
        <div className="hero-leaders-section">
          <h3>{t("our_leadership")}</h3>

          <div className="leaders-container">
            {leaders.map((member) => (
              <div className="leader-profile" key={member._id}>
                <img
                  src={member.photo}
                  alt={t(member.name)}
                  className="leader-image"
                  onError={(e) => {
                    e.target.src =
                      "https://cdn-icons-png.flaticon.com/512/149/149071.png";
                  }}
                />
                <h4>{t(member.name)}</h4>
                <p>
                  {t(member.role.toLowerCase().replace(/[\s-]/g, ""))}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
