import React, { useEffect, useState } from "react";
import "./ManageAbout.css";

const API = "https://backend-9i6n.onrender.com";

const ManageAbout = () => {
  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    name: "",
    history: "",
    area: "",
    description: "",
    populationTotal: "",
    populationMale: "",
    populationFemale: "",
    totalHouses: "",
    totalWards: "",
    wards: [
      { wardNo: "Ward 1", houses: "", total: "", male: "", female: "" },
    ],
  });

  const token = localStorage.getItem("token");

  /* ================= FETCH ================= */
  useEffect(() => {
    fetch(`${API}/api/village`)
      .then((res) => res.json())
      .then((data) => {
  if (data) {
    setForm({
      ...data,
      wards: Array.isArray(data.wards)
        ? data.wards
        : [
            {
              wardNo: "Ward 1",
              houses: "",
              total: "",
              male: "",
              female: "",
            },
          ],
    });
  }
})

      .catch((err) => console.error(err));
  }, []);

  /* ================= HANDLE CHANGE ================= */
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleWardChange = (index, e) => {
    const updatedWards = [...form.wards];
    updatedWards[index][e.target.name] = e.target.value;
    setForm({ ...form, wards: updatedWards });
  };

  const addWard = () => {
    setForm({
      ...form,
      wards: [
        ...form.wards,
        {
          wardNo: `Ward ${form.wards.length + 1}`,
          houses: "",
          total: "",
          male: "",
          female: "",
        },
      ],
    });
  };

  /* ================= SAVE ================= */
  const saveData = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`${API}/api/village`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });

      if (!res.ok) throw new Error("Save failed");

      alert("‚úÖ Village details updated successfully");
      setEditMode(false);
    } catch (err) {
      alert("‚ùå Failed to update");
    }
  };

  return (
    <div className="manage-about">
      <h2>Village Information (Admin)</h2>

      {/* ================= VIEW MODE ================= */}
      {!editMode && (
        <div className="about-card">
          <h3>{form.name}</h3>
          <p>{form.history}</p>

          <div className="stats">
            <div>üè† Houses: <b>{form.totalHouses}</b></div>
            <div>üß± Wards: <b>{form.totalWards}</b></div>
            <div>üë• Total: <b>{form.populationTotal}</b></div>
            <div>üë® Male: <b>{form.populationMale}</b></div>
            <div>üë© Female: <b>{form.populationFemale}</b></div>
            <div>üìê Area: <b>{form.area}</b></div>
          </div>

          <h4>Ward-wise Population</h4>

          <table className="ward-table">
            <thead>
              <tr>
                <th>Ward</th>
                <th>Houses</th>
                <th>Total</th>
                <th>Male</th>
                <th>Female</th>
              </tr>
            </thead>
            <tbody>
              {form.wards.map((w, i) => (
                <tr key={i}>
                  <td>{w.wardNo}</td>
                  <td>{w.houses}</td>
                  <td>{w.total}</td>
                  <td>{w.male}</td>
                  <td>{w.female}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <button className="edit-btn" onClick={() => setEditMode(true)}>
            Edit
          </button>
        </div>
      )}

      {/* ================= EDIT MODE ================= */}
      {editMode && (
        <form className="about-form" onSubmit={saveData}>
          <input name="name" placeholder="Village Name" value={form.name} onChange={handleChange} />
          <textarea name="history" placeholder="Village History" value={form.history} onChange={handleChange} />

          <input name="totalHouses" placeholder="Total Houses" value={form.totalHouses} onChange={handleChange} />
          <input name="totalWards" placeholder="Total Wards" value={form.totalWards} onChange={handleChange} />

          <input name="populationTotal" placeholder="Total Population" value={form.populationTotal} onChange={handleChange} />
          <input name="populationMale" placeholder="Male Population" value={form.populationMale} onChange={handleChange} />
          <input name="populationFemale" placeholder="Female Population" value={form.populationFemale} onChange={handleChange} />

          <input name="area" placeholder="Area (sq km)" value={form.area} onChange={handleChange} />
          <textarea name="description" placeholder="Extra Description" value={form.description} onChange={handleChange} />

          <h4>Ward Details</h4>

          {form.wards.map((ward, index) => (
            <div className="ward-row" key={index}>
              <input name="wardNo" value={ward.wardNo} onChange={(e) => handleWardChange(index, e)} />
              <input name="houses" placeholder="Houses" value={ward.houses} onChange={(e) => handleWardChange(index, e)} />
              <input name="total" placeholder="Total" value={ward.total} onChange={(e) => handleWardChange(index, e)} />
              <input name="male" placeholder="Male" value={ward.male} onChange={(e) => handleWardChange(index, e)} />
              <input name="female" placeholder="Female" value={ward.female} onChange={(e) => handleWardChange(index, e)} />
            </div>
          ))}

          <button type="button" onClick={addWard}>
            + Add Ward
          </button>

          <div className="form-actions">
            <button type="submit">Save</button>
            <button type="button" onClick={() => setEditMode(false)}>Cancel</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ManageAbout;
