import React, { useEffect, useState } from "react";
import "./ManageContacts.css";

const ManageContacts = () => {
  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [photo, setPhoto] = useState(null);
  const [members, setMembers] = useState([]);

  const token = localStorage.getItem("token");

  const fetchMembers = async () => {
    const res = await fetch("http://localhost:5000/api/contacts");
    const data = await res.json();
    setMembers(data);
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  const addMember = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("role", role);
    formData.append("phone", phone);
    formData.append("email", email);
    formData.append("photo", photo);

    await fetch("http://localhost:5000/api/contacts", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    setName("");
    setRole("");
    setPhone("");
    setEmail("");
    setPhoto(null);
    fetchMembers();
  };

  const deleteMember = async (id) => {
    await fetch(`http://localhost:5000/api/contacts/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    fetchMembers();
  };

  return (
    <div className="manage-container">
      <h2>Manage Gram Panchayat Members</h2>

      <form className="member-form" onSubmit={addMember}>
        <input placeholder="Name" value={name} onChange={e => setName(e.target.value)} required />
        <input placeholder="Role" value={role} onChange={e => setRole(e.target.value)} required />
        <input placeholder="Phone" value={phone} onChange={e => setPhone(e.target.value)} required />
        <input placeholder="Email (optional)" value={email} onChange={e => setEmail(e.target.value)} />
        <input type="file" onChange={e => setPhoto(e.target.files[0])} />
        <button type="submit">Add Member</button>
      </form>

      <div className="member-grid">
        {members.map(m => (
          <div className="member-card" key={m._id}>
            <img
              src={`http://localhost:5000${m.photo}`}
              alt={m.name}
            />
            <h4>{m.name}</h4>
            <p>{m.role}</p>
            <p>ðŸ“ž {m.phone}</p>
            <button onClick={() => deleteMember(m._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageContacts;
