import React, { useEffect, useState } from "react";
import "./Contact.css";

const API = process.env.REACT_APP_API_URL; // ‚úÖ Backend URL

const Contact = () => {
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const res = await fetch(`${API}/api/contacts`);
        if (!res.ok) throw new Error("Failed to fetch contacts");

        const data = await res.json();
        setMembers(data);
      } catch (err) {
        console.error("Contact fetch error:", err);
        setError("Unable to load contacts");
      } finally {
        setLoading(false);
      }
    };

    fetchContacts();
  }, []);

  /* ================= LOADING ================= */
  if (loading) {
    return (
      <div className="contact-page">
        <div className="loading-container">
          <div className="spinner"></div>
          <p>Loading contact details...</p>
        </div>
      </div>
    );
  }

  /* ================= ERROR ================= */
  if (error) {
    return (
      <div className="contact-page">
        <div className="error-container">
          <p className="error-message">‚ö†Ô∏è {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="contact-page">
      {/* HEADER */}
      <div className="contact-header">
        <h1 className="contact-title">
          Gram Panchayat Contact Directory
        </h1>
        <p className="contact-subtitle">
          Connect with our dedicated team members
        </p>
      </div>

      {/* CONTACT GRID */}
      <div className="contact-grid">
        {members.map((m) => {
          // ‚úÖ SAFE IMAGE HANDLING
          const imageSrc = m.photo?.startsWith("http")
            ? m.photo
            : `${API}${m.photo}`;

          return (
            <div className="contact-card" key={m._id}>
              <div className="card-image-wrapper">
                <img
                  src={imageSrc}
                  alt={m.name}
                  className="contact-img"
                  loading="lazy"
                  onError={(e) => {
                    e.target.src =
                      "https://via.placeholder.com/200?text=No+Image";
                  }}
                />
              </div>

              <div className="card-content">
                <h3 className="contact-name">{m.name}</h3>
                <p className="contact-role">{m.role}</p>

                <div className="contact-details">
                  {m.phone && (
                    <a
                      href={`tel:${m.phone}`}
                      className="contact-info phone"
                    >
                      üìû {m.phone}
                    </a>
                  )}

                  {m.email && (
                    <a
                      href={`mailto:${m.email}`}
                      className="contact-info email"
                    >
                      ‚úâÔ∏è {m.email}
                    </a>
                  )}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* EMPTY STATE */}
      {members.length === 0 && (
        <div className="empty-state">
          <p>No contacts available at the moment.</p>
        </div>
      )}
    </div>
  );
};

export default Contact;
