import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);

  const [notices, setNotices] = useState([]);
  
  const [editPopup, setEditPopup] = useState(false);
  const [currentEdit, setCurrentEdit] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");

  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  // ================= FETCH =================
  const fetchNotices = async () => {
    const res = await fetch("http://localhost:5000/api/notices");
    const data = await res.json();
    setNotices(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  // ================= ADD =================
  const addNotice = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("isImportant", isImportant);
    if (file) formData.append("attachment", file);

    await fetch("http://localhost:5000/api/notices", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    setTitle("");
    setDescription("");
    setIsImportant(false);
    setFile(null);
    setMessage("‚úÖ Notice added successfully");
    setTimeout(() => setMessage(""), 3000);
    fetchNotices();
  };

  // ================= DELETE =================
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    await fetch(`http://localhost:5000/api/notices/${id}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    setMessage("üóë Notice deleted");
    setTimeout(() => setMessage(""), 3000);
    fetchNotices();
  };

  // ================= OPEN EDIT POPUP =================
  const openEditPopup = (notice) => {
    setCurrentEdit(notice);
    setEditTitle(notice.title);
    setEditDescription(notice.description);
    setEditPopup(true);
  };

  // ================= UPDATE =================
  const updateNotice = async () => {
    if (!editTitle.trim() || !editDescription.trim()) {
      alert("Title and description cannot be empty");
      return;
    }

    await fetch(`http://localhost:5000/api/notices/${currentEdit._id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        title: editTitle,
        description: editDescription,
      }),
    });

    setEditPopup(false);
    setCurrentEdit(null);
    setMessage("‚úèÔ∏è Notice updated successfully");
    setTimeout(() => setMessage(""), 3000);
    fetchNotices();
  };

  return (
    <div className="manage-notices">
      <h2>üì¢ Manage Notices</h2>
      {message && <p className="success-msg">{message}</p>}

      {/* ADD FORM */}
      <form className="notice-form" onSubmit={addNotice}>
        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label className="important-checkbox">
          <input
            type="checkbox"
            checked={isImportant}
            onChange={(e) => setIsImportant(e.target.checked)}
          />
          ‚≠ê Mark as Important
        </label>

        <input
          type="file"
          accept=".pdf,.doc,.docx"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button type="submit">‚ûï Add Notice</button>
      </form>

      {/* NOTICE LIST */}
      <div className="notice-list">
        {notices.map((n) => (
          <div
            className={`notice-card ${n.isImportant ? "important" : ""}`}
            key={n._id}
          >
            {n.isImportant && <div className="important-badge">‚≠ê Important</div>}
            
            <h4>{n.title}</h4>
            <p>{n.description.length > 100 ? `${n.description.slice(0, 100)}...` : n.description}</p>

            {n.attachment && (
              <a
                href={`http://localhost:5000${n.attachment}`}
                target="_blank"
                rel="noopener noreferrer"
                className="attachment-link"
              >
                üìé View Attachment
              </a>
            )}

            <div className="card-actions">
              <button className="edit-btn" onClick={() => openEditPopup(n)}>
                ‚úèÔ∏è Edit
              </button>
              <button
                className="delete-btn"
                onClick={() => deleteNotice(n._id)}
              >
                üóë Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* EDIT POPUP */}
      {editPopup && (
        <div className="edit-overlay">
          <div className="edit-modal">
            <h3>‚úèÔ∏è Edit Notice</h3>

            <input
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              placeholder="Notice Title"
            />

            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="Notice Description"
              rows="8"
            />

            {currentEdit.attachment && (
              <div className="current-attachment">
                <p>üìé Current Attachment:</p>
                <a
                  href={`http://localhost:5000${currentEdit.attachment}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View Current Attachment
                </a>
              </div>
            )}

            <div className="modal-actions">
              <button className="save-btn" onClick={updateNotice}>
                üíæ Save Changes
              </button>
              <button
                className="cancel-btn"
                onClick={() => {
                  setEditPopup(false);
                  setCurrentEdit(null);
                }}
              >
                ‚ùå Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageNotices;
