import React, { useEffect, useState } from "react";
import "./ManageServices.css";

const API = "https://backend-9i6n.onrender.com";

const ManageServices = () => {
  const [applications, setApplications] = useState([]);
  const [activeFilter, setActiveFilter] = useState("Pending");

  const [showRejectModal, setShowRejectModal] = useState(false);
  const [showApproveModal, setShowApproveModal] = useState(false);

  const [messageText, setMessageText] = useState("");
  const [selectedAppId, setSelectedAppId] = useState(null);

  const token = localStorage.getItem("token");

  /* ================= FETCH ================= */
  const fetchApplications = async () => {
    const res = await fetch(`${API}/api/admin/services`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    const data = await res.json();
    setApplications(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  /* ================= UPDATE STATUS ================= */
  const updateStatus = async (id, status, adminRemark = "") => {
    await fetch(`${API}/api/admin/services/${id}/status`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ status, adminRemark }),
    });
    fetchApplications();
  };

  /* ================= APPROVE ================= */
  const handleApprove = (id) => {
    setSelectedAppId(id);
    setShowApproveModal(true);
  };

  const confirmApprove = () => {
    if (!messageText.trim()) {
      alert("Please enter message for user");
      return;
    }

    updateStatus(selectedAppId, "Approved", messageText);
    closeModal();
  };

  /* ================= REJECT ================= */
  const handleReject = (id) => {
    setSelectedAppId(id);
    setShowRejectModal(true);
  };

  const confirmReject = () => {
    if (!messageText.trim()) {
      alert("Please enter rejection reason");
      return;
    }

    updateStatus(selectedAppId, "Rejected", messageText);
    closeModal();
  };

  const closeModal = () => {
    setShowApproveModal(false);
    setShowRejectModal(false);
    setMessageText("");
    setSelectedAppId(null);
  };

  /* ================= FILTER ================= */
  const filteredApps = applications.filter(
    (a) => a.status === activeFilter
  );

  return (
    <div className="manage-services">
      <h2>Service Applications Management</h2>

      <div className="filter-buttons">
        {["Pending", "Approved", "Rejected"].map((f) => (
          <button
            key={f}
            className={`filter-btn ${activeFilter === f ? "active" : ""}`}
            onClick={() => setActiveFilter(f)}
          >
            {f}
          </button>
        ))}
      </div>

      <div className="cards-container">
        {filteredApps.map((app) => (
          <div className="service-card" key={app._id}>
            <p><b>Name:</b> {app.fullName}</p>
            <p><b>Service:</b> {app.serviceType}</p>
            <p><b>Mobile:</b> {app.mobile}</p>

            {activeFilter !== "Pending" && app.adminRemark && (
              <div className="admin-message">
                <b>Admin Message:</b>
                <p>{app.adminRemark}</p>
              </div>
            )}

            {activeFilter === "Pending" && (
              <div className="actions">
                <button
                  className="approve-btn"
                  onClick={() => handleApprove(app._id)}
                >
                  Approve
                </button>

                <button
                  className="reject-btn"
                  onClick={() => handleReject(app._id)}
                >
                  Reject
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* ================= APPROVE MODAL ================= */}
      {(showApproveModal || showRejectModal) && (
        <div className="modal-overlay">
          <div className="modal-box">
            <h3>
              {showApproveModal
                ? "✅ Approval Message"
                : "❌ Rejection Reason"}
            </h3>

            <textarea
              placeholder={
                showApproveModal
                  ? "Your documents are ready. Please come to Panchayat at 4 PM."
                  : "Enter rejection reason..."
              }
              value={messageText}
              onChange={(e) => setMessageText(e.target.value)}
              rows="4"
            />

            <div className="modal-actions">
              <button
                className="confirm-btn"
                onClick={
                  showApproveModal ? confirmApprove : confirmReject
                }
              >
                Confirm
              </button>
              <button className="cancel-btn" onClick={closeModal}>
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageServices;
