import React, { useEffect, useState } from "react";
import "./HeroSection.css";
import { useTranslation } from "react-i18next";

const API = "https://backend-9i6n.onrender.com";

const HeroSection = () => {
  const { t } = useTranslation();
  const [leaders, setLeaders] = useState([]);

  useEffect(() => {
    const fetchLeaders = async () => {
      try {
        const res = await fetch(`${API}/api/contacts`);
        const data = await res.json();

        const filtered = data.filter((m) => {
          const role = m.role?.toLowerCase().replace(/[\s-]/g, "");
          return role === "sarpanch" || role === "upasarpanch";
        });

        setLeaders(filtered.slice(0, 2));
      } catch (err) {
        console.error("Failed to fetch leaders", err);
      }
    };

    fetchLeaders();
  }, []);

  return (
    <div className="hero-wrapper">
      {/* image & overlays SAME */}

      <div className="hero-text-section">
        <div className="hero-badge">{t("hero_badge")}</div>

        <h1 className="hero-title">
          {t("hero_title")}
          <br />
          <span className="hero-title-accent">
            {t("hero_title_accent")}
          </span>
        </h1>

        <p className="hero-description">
          {t("hero_description")}
        </p>
      </div>

      <div className="hero-leaders-section">
        <h3>{t("our_leadership")}</h3>

        {leaders.map((member) => (
          <div key={member._id}>
            <h4>{member.name}</h4>
            <p>
              {t(member.role.toLowerCase().replace(/[\s-]/g, ""))}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HeroSection;
