import React, { useState } from "react";

const slides = [
  {
    id: 1,
    title: "Welcome to Gram Panchayat",
    description: "Empowering local communities with technology",
    image: "https://source.unsplash.com/1600x600/?village,technology",
  },
  {
    id: 2,
    title: "Community Resources",
    description: "Find and share resources in your area easily",
    image: "https://source.unsplash.com/1600x600/?community,village",
  },
  {
    id: 3,
    title: "Stay Connected",
    description: "Get real-time updates on local projects and events",
    image: "https://source.unsplash.com/1600x600/?meeting,village",
  },
];

const HeroSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const prevSlide = () => {
    const index = currentIndex === 0 ? slides.length - 1 : currentIndex - 1;
    setCurrentIndex(index);
  };

  const nextSlide = () => {
    const index = currentIndex === slides.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(index);
  };

  return (
    <div className="relative w-full h-[400px] md:h-[600px] overflow-hidden rounded-lg shadow-lg">
      {slides.map((slide, index) => (
        <div
          key={slide.id}
          className={`absolute top-0 left-0 w-full h-full transition-opacity duration-1000 ${
            index === currentIndex ? "opacity-100" : "opacity-0"
          }`}
        >
          <img
            src={slide.image}
            alt={slide.title}
            className="w-full h-full object-cover"
          />
          <div className="absolute bottom-10 left-10 bg-black bg-opacity-50 text-white p-4 rounded-md max-w-lg">
            <h2 className="text-2xl md:text-4xl font-bold">{slide.title}</h2>
            <p className="mt-2 text-sm md:text-lg">{slide.description}</p>
          </div>
        </div>
      ))}

      {/* Navigation Buttons */}
      <button
        onClick={prevSlide}
        className="absolute top-1/2 left-5 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70"
      >
        &#10094;
      </button>
      <button
        onClick={nextSlide}
        className="absolute top-1/2 right-5 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70"
      >
        &#10095;
      </button>

      {/* Dots */}
      <div className="absolute bottom-5 left-1/2 transform -translate-x-1/2 flex space-x-2">
        {slides.map((_, index) => (
          <span
            key={index}
            className={`w-3 h-3 rounded-full cursor-pointer ${
              index === currentIndex ? "bg-white" : "bg-gray-400"
            }`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default HeroSlider;
