import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css";

const Dashboard = () => {
  const [stats, setStats] = useState([]);
  const [recentActivities, setRecentActivities] = useState([]);
  const [loading, setLoading] = useState(true);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good Morning";
    if (hour < 18) return "Good Afternoon";
    return "Good Evening";
  };

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      const [
        noticesRes,
        servicesRes,
        schemesRes,
        complaintsRes
      ] = await Promise.all([
        axios.get("http://localhost:5000/api/notices"),
        axios.get("http://localhost:5000/api/admin/services"),
        axios.get("http://localhost:5000/api/schemes"),
        axios.get("http://localhost:5000/api/contacts")
      ]);

      // ‚úÖ STATS
      setStats([
        { label: "Total Notices", value: noticesRes.data.length, icon: "üì¢" },
        { label: "Active Services", value: servicesRes.data.length, icon: "üõ†Ô∏è" },
        { label: "Schemes", value: schemesRes.data.length, icon: "üìã" },
        { label: "Complaints", value: complaintsRes.data.length, icon: "üìù" }
      ]);

      // ‚úÖ RECENT ACTIVITIES (latest 5 notices)
      const activities = noticesRes.data
        .slice(0, 5)
        .map(n => ({
          action: `Notice added: ${n.title}`,
          time: new Date(n.createdAt).toLocaleString(),
          type: "notice"
        }));

      setRecentActivities(activities);

    } catch (error) {
      console.error("Dashboard fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) return <p className="loading-text">Loading dashboard...</p>;

  return (
    <div className="dashboard">

      {/* Welcome Section */}
      <div className="welcome-card">
        <div className="welcome-content">
          <h1 className="welcome-title">
            {getGreeting()}, Administrator!
          </h1>
          <p className="welcome-subtitle">
            Welcome back to your Gram Panchayat Admin Dashboard.
          </p>
        </div>
        <div className="welcome-illustration">
          <span className="illustration-icon">üèõÔ∏è</span>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        {stats.map((stat, index) => (
          <div key={index} className="stat-card">
            <div className="stat-icon">{stat.icon}</div>
            <div className="stat-details">
              <h3 className="stat-value">{stat.value}</h3>
              <p className="stat-label">{stat.label}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Recent Activities */}
      <div className="dashboard-card">
        <div className="card-header">
          <h2 className="card-title">Recent Activities</h2>
        </div>

        {recentActivities.length === 0 ? (
          <p>No recent activity</p>
        ) : (
          <div className="activities-list">
            {recentActivities.map((activity, index) => (
              <div key={index} className="activity-item">
                <div className="activity-indicator"></div>
                <div className="activity-content">
                  <p className="activity-action">{activity.action}</p>
                  <span className="activity-time">{activity.time}</span>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* System Status */}
      <div className="dashboard-card system-status">
        <div className="card-header">
          <h2 className="card-title">System Status</h2>
          <span className="status-badge status-online">
            All Systems Online
          </span>
        </div>
        <div className="status-grid">
          {["Database", "API Services", "File Storage", "Email Service"].map(
            (item, i) => (
              <div key={i} className="status-item">
                <span className="status-dot online"></span>
                <span>{item}</span>
              </div>
            )
          )}
        </div>
      </div>

    </div>
  );
};

export default Dashboard;
