import React, { useState, useContext, useEffect } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";
import { useLocation } from "react-router-dom";
import "./ApplyService.css";

const API =
  process.env.REACT_APP_API_URL || "https://backend-9i6n.onrender.com";

const MAX_FILE_SIZE = 500 * 1024;

const ApplyService = () => {
  const { token } = useContext(AuthContext);
  const location = useLocation();

  const [formData, setFormData] = useState({
    serviceType: "",
    fullName: "",
    address: "",
    mobile: "",
    deceasedName: "",
    dateOfDeath: "",
  });

  const [files, setFiles] = useState({});
  const [fileErrors, setFileErrors] = useState({});
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  /* PREFILL SERVICE */
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const serviceFromUrl = params.get("service");
    if (serviceFromUrl) {
      setFormData((p) => ({ ...p, serviceType: serviceFromUrl }));
    }
  }, [location.search]);

  /* INPUT */
  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData({ ...formData, [name]: value });
    setErrors((p) => ({ ...p, [name]: "" }));
  };

  /* FILE VALIDATION */
  const handleFileChange = (e) => {
    const { name, files: selectedFiles } = e.target;
    const file = selectedFiles[0];
    if (!file) return;

    const allowedTypes = ["image/jpeg", "image/png", "image/jpg", "application/pdf"];

    if (!allowedTypes.includes(file.type)) {
      setFileErrors((p) => ({
        ...p,
        [name]: "‡§´‡§ï‡•ç‡§§ JPG, JPEG, PNG ‡§ï‡§ø‡§Ç‡§µ‡§æ PDF ‡§´‡§æ‡§à‡§≤ ‡§Ö‡§™‡§≤‡•ã‡§° ‡§ï‡§∞‡§æ",
      }));
      e.target.value = "";
      return;
    }

    if (file.size > MAX_FILE_SIZE) {
      setFileErrors((p) => ({
        ...p,
        [name]: "‡§´‡§æ‡§à‡§≤‡§ö‡§æ ‡§Ü‡§ï‡§æ‡§∞ 500 KB ‡§™‡•á‡§ï‡•ç‡§∑‡§æ ‡§ú‡§æ‡§∏‡•ç‡§§ ‡§®‡§∏‡§æ‡§µ‡§æ",
      }));
      e.target.value = "";
      return;
    }

    setFileErrors((p) => ({ ...p, [name]: "" }));
    setFiles((p) => ({ ...p, [name]: file }));
  };

  /* FORM VALIDATION */
  const validateForm = () => {
    const newErrors = {};

    if (!formData.serviceType)
      newErrors.serviceType = "‡§ï‡•É‡§™‡§Ø‡§æ ‡§∏‡•á‡§µ‡§æ ‡§®‡§ø‡§µ‡§°‡§æ";

    if (!formData.fullName.trim())
      newErrors.fullName = "‡§™‡•Ç‡§∞‡•ç‡§£ ‡§®‡§æ‡§µ ‡§Ü‡§µ‡§∂‡•ç‡§Ø‡§ï ‡§Ü‡§π‡•á";

    if (!formData.address.trim())
      newErrors.address = "‡§™‡§§‡•ç‡§§‡§æ ‡§Ü‡§µ‡§∂‡•ç‡§Ø‡§ï ‡§Ü‡§π‡•á";

    if (!/^[6-9]\d{9}$/.test(formData.mobile))
      newErrors.mobile = "‡§µ‡•à‡§ß 10 ‡§Ö‡§Ç‡§ï‡•Ä ‡§Æ‡•ã‡§¨‡§æ‡§à‡§≤ ‡§ï‡•ç‡§∞‡§Æ‡§æ‡§Ç‡§ï ‡§ü‡§æ‡§ï‡§æ";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  /* SUBMIT */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);

    const data = new FormData();
    Object.entries(formData).forEach(([k, v]) => data.append(k, v));
    Object.entries(files).forEach(([k, f]) => data.append(k, f));

    try {
      await axios.post(`${API}/api/services/apply`, data, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("‡§Ö‡§∞‡•ç‡§ú ‡§Ø‡§∂‡§∏‡•ç‡§µ‡•Ä‡§∞‡•Ä‡§§‡•ç‡§Ø‡§æ ‡§∏‡§æ‡§¶‡§∞ ‡§ù‡§æ‡§≤‡§æ ‚úÖ");

      setFormData({
        serviceType: "",
        fullName: "",
        address: "",
        mobile: "",
        deceasedName: "",
        dateOfDeath: "",
      });
      setFiles({});
      setFileErrors({});
      setErrors({});
    } catch {
      alert("‡§Ö‡§∞‡•ç‡§ú ‡§∏‡§æ‡§¶‡§∞ ‡§π‡•ã‡§ä ‡§∂‡§ï‡§≤‡§æ ‡§®‡§æ‡§π‡•Ä ‚ùå");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="apply-service-container">
      <h2>‡§∏‡•á‡§µ‡•á‡§∏‡§æ‡§†‡•Ä ‡§Ö‡§∞‡•ç‡§ú ‡§ï‡§∞‡§æ</h2>

      <form onSubmit={handleSubmit}>
        {/* SERVICE */}
        <label>‡§∏‡•á‡§µ‡§æ ‡§®‡§ø‡§µ‡§°‡§æ</label>
        <select
          name="serviceType"
          value={formData.serviceType}
          onChange={handleChange}
          className={errors.serviceType ? "error" : ""}
        >
          <option value="">-- ‡§∏‡•á‡§µ‡§æ ‡§®‡§ø‡§µ‡§°‡§æ --</option>
          <option value="‡§ú‡§®‡•ç‡§Æ ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§ú‡§®‡•ç‡§Æ ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§Æ‡•É‡§§‡•ç‡§Ø‡•Ç ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§Æ‡•É‡§§‡•ç‡§Ø‡•Ç ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§â‡§§‡•ç‡§™‡§®‡•ç‡§® ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§â‡§§‡•ç‡§™‡§®‡•ç‡§® ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§∞‡§π‡§ø‡§µ‡§æ‡§∏‡•Ä ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§∞‡§π‡§ø‡§µ‡§æ‡§∏‡•Ä ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
          <option value="‡§µ‡§ø‡§µ‡§æ‡§π ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞">‡§µ‡§ø‡§µ‡§æ‡§π ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞</option>
        </select>
        {errors.serviceType && <p className="file-error">{errors.serviceType}</p>}

        {/* APPLICANT */}
        <h4>‡§Ö‡§∞‡•ç‡§ú‡§¶‡§æ‡§∞‡§æ‡§ö‡•Ä ‡§Æ‡§æ‡§π‡§ø‡§§‡•Ä</h4>

        <input
          name="fullName"
          placeholder="‡§™‡•Ç‡§∞‡•ç‡§£ ‡§®‡§æ‡§µ"
          onChange={handleChange}
          className={errors.fullName ? "error" : ""}
        />
        {errors.fullName && <p className="file-error">{errors.fullName}</p>}

        <input
          name="address"
          placeholder="‡§™‡§§‡•ç‡§§‡§æ"
          onChange={handleChange}
          className={errors.address ? "error" : ""}
        />
        {errors.address && <p className="file-error">{errors.address}</p>}

        <input
          name="mobile"
          placeholder="‡§Æ‡•ã‡§¨‡§æ‡§à‡§≤ ‡§ï‡•ç‡§∞‡§Æ‡§æ‡§Ç‡§ï"
          onChange={handleChange}
          className={errors.mobile ? "error" : ""}
        />
        {errors.mobile && <p className="file-error">{errors.mobile}</p>}

        {/* ‡§ú‡§®‡•ç‡§Æ ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞ FILE */}
        {formData.serviceType === "‡§ú‡§®‡•ç‡§Æ ‡§™‡•ç‡§∞‡§Æ‡§æ‡§£‡§™‡§§‡•ç‡§∞" && (
