import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";
import "./ManageContacts.css";

const API = process.env.REACT_APP_API_URL;

const ManageContacts = () => {
  const token = localStorage.getItem("token");

  if (!token) {
    return <p className="no-auth">Please login as admin</p>;
  }

  const [members, setMembers] = useState([]);
  const [loadingList, setLoadingList] = useState(true);
  const [loadingBtn, setLoadingBtn] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const [name, setName] = useState("");
  const [role, setRole] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [photo, setPhoto] = useState(null);
  const [preview, setPreview] = useState(null);

  const roles = [
    "Sarpanch",
    "Upa-Sarpanch",
    "Ward Member",
    "Gram Sevak",
    "VDO",
    "Rozgar Sevak",
    "Anganwadi Worker",
    "ASHA Worker",
  ];

  // FETCH MEMBERS
  const fetchMembers = async () => {
    setLoadingList(true);
    try {
      const res = await fetch(`${API}/api/contacts`);
      const data = await res.json();
      setMembers(data);
    } catch (err) {
      toast.error("Failed to load members");
    } finally {
      setLoadingList(false);
    }
  };

  useEffect(() => {
    fetchMembers();
  }, []);

  // PHOTO PREVIEW
  const handlePhoto = (e) => {
    const file = e.target.files[0];
    setPhoto(file);
    if (file) setPreview(URL.createObjectURL(file));
  };

  // ADD MEMBER
  const addMember = async (e) => {
    e.preventDefault();
    setLoadingBtn(true);
    setUploadProgress(0);

    const formData = new FormData();
    formData.append("name", name);
    formData.append("role", role);
    formData.append("phone", phone);
    formData.append("email", email);
    if (photo) formData.append("photo", photo);

    try {
      await axios.post(`${API}/api/contacts`, formData, {
        headers: { Authorization: `Bearer ${token}` },
        onUploadProgress: (e) => {
          const percent = Math.round((e.loaded * 100) / e.total);
          setUploadProgress(percent);
        },
      });

      toast.success("Member added successfully ✅");

      setName("");
      setRole("");
      setPhone("");
      setEmail("");
      setPhoto(null);
      setPreview(null);

      fetchMembers();
    } catch (err) {
      toast.error("Failed to add member ❌");
    } finally {
      setLoadingBtn(false);
      setUploadProgress(0);
    }
  };

  return (
    <div className="manage-container">
      <h1 className="page-title">Manage Gram Panchayat Members</h1>

      {/* ADD FORM */}
      <form className="member-form" onSubmit={addMember}>
        <input
          placeholder="Full Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <select value={role} onChange={(e) => setRole(e.target.value)} required>
          <option value="">Select Role</option>
          {roles.map((r) => (
            <option key={r}>{r}</option>
          ))}
        </select>

        <input
          placeholder="Phone"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />

        <input
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <input type="file" accept="image/*" onChange={handlePhoto} />

        {preview && <img src={preview} alt="preview" className="preview-img" />}

        {uploadProgress > 0 && (
          <div className="progress-bar">
            <div
              className="progress-fill"
              style={{ width: `${uploadProgress}%` }}
            />
          </div>
        )}

        <button disabled={loadingBtn} className="submit-btn">
          {loadingBtn ? <span className="btn-loader"></span> : "Add Member"}
        </button>
      </form>

      {/* MEMBERS LIST */}
      <h2 className="section-title">Members</h2>

      {loadingList ? (
        <div className="member-grid">
          {[...Array(4)].map((_, i) => (
            <div className="member-card skeleton" key={i}>
              <div className="skeleton-avatar"></div>
              <div className="skeleton-line"></div>
              <div className="skeleton-line small"></div>
            </div>
          ))}
        </div>
      ) : (
        <div className="member-grid">
          {members.map((m) => (
            <div className="member-card" key={m._id}>
              <img src={m.photo} alt={m.name} />
              <h3>{m.name}</h3>
              <p>{m.role}</p>
              <p>{m.phone}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ManageContacts;
