import React, { useEffect, useState } from "react";
import "./ManageServices.css";

/* ‚úÖ CLOUD BACKEND */
const API = "https://backend-9i6n.onrender.com";

const ManageServices = () => {
  const [applications, setApplications] = useState([]);
  const [activeFilter, setActiveFilter] = useState("Pending");

  const [showRejectModal, setShowRejectModal] = useState(false);
  const [remarkText, setRemarkText] = useState("");
  const [selectedAppId, setSelectedAppId] = useState(null);

  const token = localStorage.getItem("token");

  /* ================= FETCH APPLICATIONS ================= */
  const fetchApplications = async () => {
    try {
      const res = await fetch(`${API}/api/admin/services`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      setApplications(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Fetch failed:", err);
    }
  };

  useEffect(() => {
    fetchApplications();
  }, []);

  /* ================= COUNTS ================= */
  const pending = applications.filter(a => a.status === "Pending");
  const approved = applications.filter(a => a.status === "Approved");
  const rejected = applications.filter(a => a.status === "Rejected");

  const filteredApps =
    activeFilter === "Approved"
      ? approved
      : activeFilter === "Rejected"
      ? rejected
      : pending;

  /* ================= UPDATE STATUS ================= */
  const updateStatus = async (id, status, adminRemark = "") => {
    try {
      await fetch(`${API}/api/admin/services/${id}/status`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status, adminRemark }),
      });

      fetchApplications();
    } catch (err) {
      console.error("Status update failed:", err);
    }
  };

  /* ================= REJECT / APPROVE MESSAGE ================= */
  const openRemarkModal = (id, type) => {
    setSelectedAppId(id);
    setRemarkText("");
    setShowRejectModal(type);
  };

  const confirmAction = () => {
    if (!remarkText.trim()) {
      alert("Please enter message");
      return;
    }

    updateStatus(
      selectedAppId,
      showRejectModal === "approve" ? "Approved" : "Rejected",
      remarkText
    );

    setShowRejectModal(false);
    setRemarkText("");
    setSelectedAppId(null);
  };

  return (
    <div className="manage-services">
      <h2>Service Applications Management</h2>

      {/* ================= FILTER BUTTONS WITH COUNT ================= */}
      <div className="filter-buttons">
        <button
          className={`filter-btn pending-btn ${activeFilter === "Pending" ? "active" : ""}`}
          onClick={() => setActiveFilter("Pending")}
        >
          üïí Pending ({pending.length})
        </button>

        <button
          className={`filter-btn approved-btn ${activeFilter === "Approved" ? "active" : ""}`}
          onClick={() => setActiveFilter("Approved")}
        >
          ‚úÖ Approved ({approved.length})
        </button>

        <button
          className={`filter-btn rejected-btn ${activeFilter === "Rejected" ? "active" : ""}`}
          onClick={() => setActiveFilter("Rejected")}
        >
          ‚ùå Rejected ({rejected.length})
        </button>
      </div>

      {/* ================= APPLICATION LIST ================= */}
      <div className="cards-container">
        {filteredApps.length === 0 && (
          <p className="no-data">No {activeFilter.toLowerCase()} applications</p>
        )}

        {filteredApps.map(app => (
          <div className="service-card" key={app._id}>
            <p><b>Name:</b> {app.fullName}</p>
            <p><b>Service:</b> {app.serviceType}</p>
            <p><b>Mobile:</b> {app.mobile}</p>
            <p><b>Address:</b> {app.address}</p>
            <p><b>Date:</b> {new Date(app.createdAt).toLocaleDateString()}</p>

            {/* ================= DOCUMENT ICONS ================= */}
            {app.documents && (
              <>
                <p><b>üìÑ Documents:</b> {Object.keys(app.documents).length}</p>

                <div className="documents-list">
                  {Object.entries(app.documents).map(([key, url], index) => (
                    <a
                      key={index}
                      href={url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="doc-icon"
                      title={key}
                    >
                      üìé
                    </a>
                  ))}
                </div>
              </>
            )}

            {/* ================= REJECTED MESSAGE ================= */}
            {activeFilter === "Rejected" && app.adminRemark && (
              <div className="rejection-reason">
                <b>Message:</b>
                <p>{app.adminRemark}</p>
              </div>
            )}

            {/* ================= ACTION BUTTONS ================= */}
            {activeFilter === "Pending" && (
              <div className="actions">
                <button
                  className="approve-btn"
                  onClick={() => openRemarkModal(app._id, "approve")}
                >
                  Approve
                </button>

                <button
                  className="reject-btn"
                  onClick={() => openRemarkModal(app._id, "reject")}
                >
                  Reject
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* ================= MESSAGE MODAL ================= */}
      {showRejectModal && (
        <div className="modal-overlay">
          <div className="reject-modal">
            <h3>
              {showRejectModal === "approve"
                ? "‚úÖ Approval Message"
                : "‚ùå Rejection Reason"}
            </h3>

            <textarea
              placeholder="Enter message for user..."
              value={remarkText}
              onChange={(e) => setRemarkText(e.target.value)}
              rows="4"
            />

            <div className="modal-actions">
              <button className="confirm-reject-btn" onClick={confirmAction}>
                Confirm
              </button>
              <button
                className="cancel-btn"
                onClick={() => setShowRejectModal(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageServices;
