import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const API =
  process.env.REACT_APP_API_URL ||
  "https://backend-9i6n.onrender.com";

const MAX_FILE_SIZE = 10 * 1024 * 1024; // 10MB

const ManageNotices = () => {
  const token = localStorage.getItem("token");

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isImportant, setIsImportant] = useState(false);
  const [file, setFile] = useState(null);
  const [filePreview, setFilePreview] = useState(null);

  const [notices, setNotices] = useState([]);

  const [editPopup, setEditPopup] = useState(false);
  const [currentEdit, setCurrentEdit] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editFile, setEditFile] = useState(null);
  const [editPreview, setEditPreview] = useState(null);

  const [isAdding, setIsAdding] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [isDeleting, setIsDeleting] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);

  const [popup, setPopup] = useState({ show: false, message: "", type: "" });

  /* ================= POPUP ================= */
  const showPopup = (message, type = "success") => {
    setPopup({ show: true, message, type });
    setTimeout(() => setPopup({ show: false, message: "", type: "" }), 2000);
  };

  /* ================= FETCH ================= */
  const fetchNotices = async () => {
    const res = await fetch(`${API}/api/notices`);
    const data = await res.json();
    setNotices(Array.isArray(data) ? data : []);
  };

  useEffect(() => {
    fetchNotices();
  }, []);

  /* ================= FILE HANDLER ================= */
  const handleFile = (file, setPreview, setFileState) => {
    if (!file) return;

    if (file.size > MAX_FILE_SIZE) {
      showPopup("File size must be less than 10MB", "error");
      return;
    }

    setFileState(file);

    if (file.type.startsWith("image")) {
      setPreview(URL.createObjectURL(file));
    } else if (file.type === "application/pdf") {
      setPreview("pdf");
    } else {
      setPreview("doc");
    }
  };

  /* ================= ADD ================= */
  const addNotice = async (e) => {
    e.preventDefault();
    setIsAdding(true);
    setUploadProgress(0);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("isImportant", isImportant);
    if (file) formData.append("attachment", file);

    try {
      const xhr = new XMLHttpRequest();
      xhr.open("POST", `${API}/api/notices`);
      xhr.setRequestHeader("Authorization", `Bearer ${token}`);

      xhr.upload.onprogress = (e) => {
        if (e.lengthComputable) {
          setUploadProgress(Math.round((e.loaded / e.total) * 100));
        }
      };

      xhr.onload = () => {
        if (xhr.status === 201 || xhr.status === 200) {
          showPopup("Notice added successfully");
          setTitle("");
          setDescription("");
          setIsImportant(false);
          setFile(null);
          setFilePreview(null);
          fetchNotices();
        } else {
          showPopup("Upload failed", "error");
        }
        setIsAdding(false);
      };

      xhr.send(formData);
    } catch {
      showPopup("Upload error", "error");
      setIsAdding(false);
    }
  };

  /* ================= DELETE ================= */
  const deleteNotice = async (id) => {
    if (!window.confirm("Delete this notice?")) return;

    setIsDeleting(id);
    await fetch(`${API}/api/notices/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });

    showPopup("Notice deleted");
    fetchNotices();
    setIsDeleting(null);
  };

  /* ================= EDIT ================= */
  const openEditPopup = (n) => {
    setCurrentEdit(n);
    setEditTitle(n.title);
    setEditDescription(n.description);
    setEditFile(null);
    setEditPreview(null);
    setEditPopup(true);
  };

  const updateNotice = async () => {
    if (editFile && !window.confirm("Replace existing attachment?")) return;

    setIsUpdating(true);

    const formData = new FormData();
    formData.append("title", editTitle);
    formData.append("description", editDescription);
    if (editFile) formData.append("attachment", editFile);

    await fetch(`${API}/api/notices/${currentEdit._id}`, {
      method: "PUT",
      headers: { Authorization: `Bearer ${token}` },
      body: formData,
    });

    showPopup("Notice updated");
    setEditPopup(false);
    fetchNotices();
    setIsUpdating(false);
  };

  return (
    <div className="manage-notices">
      {popup.show && (
        <div className={`toast ${popup.type}`}>{popup.message}</div>
      )}

      <form className="notice-form" onSubmit={addNotice}>
        <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required />
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />

        <input
          type="file"
          accept=".pdf,.jpg,.jpeg,.png"
          onChange={(e) => handleFile(e.target.files[0], setFilePreview, setFile)}
        />

        {filePreview && filePreview !== "doc" && (
          filePreview === "pdf" ? (
            <iframe title="pdf" src={URL.createObjectURL(file)} className="pdf-preview" />
          ) : (
            <img src={filePreview} alt="preview" className="img-preview" />
          )
        )}

        {uploadProgress > 0 && <div className="progress">{uploadProgress}%</div>}

        <button disabled={isAdding}>{isAdding ? "Uploading..." : "Add Notice"}</button>
      </form>

      <div className="notice-list">
        {notices.map((n) => (
          <div key={n._id} className="notice-card">
            <h4>{n.title}</h4>
            <p>{n.description}</p>

            {n.attachment && (
              <a href={n.attachment} target="_blank" rel="noopener noreferrer">
                ðŸ“„ View / Download
              </a>
            )}

            <button onClick={() => openEditPopup(n)}>Edit</button>
            <button onClick={() => deleteNotice(n._id)}>
              {isDeleting === n._id ? "Deleting..." : "Delete"}
            </button>
          </div>
        ))}
      </div>

      {editPopup && (
        <div className="modal">
          <input value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
          <textarea value={editDescription} onChange={(e) => setEditDescription(e.target.value)} />
          <input type="file" onChange={(e) => handleFile(e.target.files[0], setEditPreview, setEditFile)} />
          <button onClick={updateNotice}>{isUpdating ? "Saving..." : "Save"}</button>
        </div>
      )}
    </div>
  );
};

export default ManageNotices;
