import React, { useEffect, useState } from "react";
import "./ManageNotices.css";

const ManageNotices = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [notices, setNotices] = useState([]);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  // FETCH NOTICES
 const fetchNotices = async () => {
  try {
    const res = await fetch("http://localhost:5000/api/notices");

    if (!res.ok) {
      throw new Error("API not reachable");
    }

    const data = await res.json();
    setNotices(data);
  } catch (error) {
    console.error("Fetch failed:", error.message);
    alert("Backend not reachable");
  }
};

  useEffect(() => {
    fetchNotices();
  }, []);

  // ADD NOTICE
  const addNotice = async (e) => {
    e.preventDefault();
    setMessage("");
    setError("");

    try {
      const res = await fetch("http://localhost:5000/api/notices", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ title, description }),
      });

      const data = await res.json();

      if (!res.ok) {
        throw new Error(data.message || "Failed to add notice");
      }

      setMessage("✅ Notice added successfully");
      setTitle("");
      setDescription("");
      fetchNotices();
    } catch (err) {
      setError(err.message || "Something went wrong");
    }
  };

  // DELETE NOTICE
  const deleteNotice = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/notices/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        throw new Error("Failed to delete notice");
      }

      fetchNotices();
    } catch (err) {
      alert("Error deleting notice");
    }
  };

  return (
    <div className="manage-notices">
      <h2>Manage Notices</h2>

      <form className="notice-form" onSubmit={addNotice}>
        {message && <p className="success-msg">{message}</p>}
        {error && <p className="error-msg">{error}</p>}

        <input
          placeholder="Notice Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Notice Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <button type="submit">Add Notice</button>
      </form>

      <div className="notice-list">
        {notices.map((n) => (
          <div className="notice-card" key={n._id}>
            <h4>{n.title}</h4>
            <p>{n.description}</p>
            <button onClick={() => deleteNotice(n._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManageNotices;
