import React, { useEffect, useState } from "react";
import "./ManageDevelopment.css";

const ManageDevelopment = () => {
  const [projects, setProjects] = useState([]);
  const [form, setForm] = useState({
    projectName: "",
    description: "",
    progress: 0,
    fundsUsed: "",
  });

  const token = localStorage.getItem("token");

  const fetchProjects = async () => {
    const res = await fetch("http://localhost:5000/api/development");
    const data = await res.json();
    setProjects(data);
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const addProject = async (e) => {
    e.preventDefault();

    await fetch("http://localhost:5000/api/development", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(form),
    });

    setForm({
      projectName: "",
      description: "",
      progress: 0,
      fundsUsed: "",
    });

    fetchProjects();
  };

  return (
    <div className="manage-development">
      <h2>Village Development Projects</h2>

      <form className="dev-form" onSubmit={addProject}>
        <input name="projectName" placeholder="Project Name" value={form.projectName} onChange={handleChange} required />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} />
        <input name="fundsUsed" placeholder="Funds Used (â‚¹)" value={form.fundsUsed} onChange={handleChange} />
        <input type="number" name="progress" placeholder="Progress %" value={form.progress} onChange={handleChange} min="0" max="100" required />
        <button>Add Project</button>
      </form>
    </div>
  );
};

export default ManageDevelopment;
